import React, { useState } from "react";
import "./QuizApp.css";
import "."

function CreateForm({ examConfig }) {
  const [questions, setQuestions] = useState(
    Array.from({ length: examConfig.questionsCount }, (_, i) => ({
      questionText: "",
      options: ["", "", "", ""],
      correctAnswer: 0,
    }))
  );

  const handleQuestionChange = (index, field, value) => {
    const updated = [...questions];
    updated[index][field] = value;
    setQuestions(updated);
  };

  const handleOptionChange = (qIndex, oIndex, value) => {
    const updated = [...questions];
    updated[qIndex].options[oIndex] = value;
    setQuestions(updated);
  };

  const handleCorrectAnswerChange = (qIndex, value) => {
    const updated = [...questions];
    updated[qIndex].correctAnswer = parseInt(value);
    setQuestions(updated);
  };

  const handleSubmit = () => {
    const fullExam = {
      ...examConfig,
      questions,
    };
    console.log("Đề thi hoàn chỉnh:", fullExam);
    alert("Đề thi đã được tạo thành công!");
    // TODO: gửi về server nếu cần
  };

  return (
    <div className="create-exam-container">
      <h2 className="form-title">Nhập câu hỏi</h2>
      <h4 className="form-subtitle">
        Tên đề: {examConfig.examName} | {examConfig.grade} -{" "}
        {examConfig.subject}
      </h4>

      {questions.map((q, index) => (
        <div key={index} className="question-box">
          <h4>Câu {index + 1}</h4>
          <input
            className="form-input"
            placeholder="Nhập nội dung câu hỏi"
            value={q.questionText}
            onChange={(e) =>
              handleQuestionChange(index, "questionText", e.target.value)
            }
          />

          {q.options.map((opt, optIndex) => (
            <input
              key={optIndex}
              className="form-input"
              placeholder={`Đáp án ${String.fromCharCode(65 + optIndex)}`}
              value={opt}
              onChange={(e) =>
                handleOptionChange(index, optIndex, e.target.value)
              }
            />
          ))}

          <label>Đáp án đúng:</label>
          <select
            className="form-input"
            value={q.correctAnswer}
            onChange={(e) => handleCorrectAnswerChange(index, e.target.value)}
          >
            <option value={0}>A</option>
            <option value={1}>B</option>
            <option value={2}>C</option>
            <option value={3}>D</option>
          </select>
        </div>
      ))}

      <button className="button-black" onClick={handleSubmit}>
        Hoàn tất và lưu đề
      </button>
    </div>
  );
}

export default CreateForm;
