import React, { useState } from "react";
import "./QuizApp.css"; // <-- Th√™m d√≤ng n√†y

function QuizApp({ examConfig, studentInfo }) {
  const codeIndex = parseInt(studentInfo.examCode.replace("MD", "")) - 1;
  const currentCode = examConfig.examCodes[codeIndex];

  const [answers, setAnswers] = useState(
    Array(currentCode.questions).fill(null)
  );

  const handleAnswerChange = (index, value) => {
    const updated = [...answers];
    updated[index] = value;
    setAnswers(updated);
  };

  const handleSubmit = () => {
    let correctCount = 0;
    answers.forEach((ans, i) => {
      if (ans === currentCode.answers[i].correct) {
        correctCount++;
      }
    });

    const scorePerQuestion = currentCode.totalScore / currentCode.questions;
    const totalScore = (correctCount * scorePerQuestion).toFixed(2);

    alert(`‚úÖ N·ªôp b√†i th√†nh c√¥ng!\nS·ªë c√¢u ƒë√∫ng: ${correctCount}/${currentCode.questions}\nƒêi·ªÉm: ${totalScore}`);
  };

  return (
    <div className="quiz-container">
      <div className="quiz-header">
        <h2>üìù B√†i thi: {examConfig.examName}</h2>
      </div>

      <div className="quiz-info">
        <p><strong>Sinh vi√™n:</strong> {studentInfo.studentName} ({studentInfo.studentId})</p>
        <p><strong>M√£ ƒë·ªÅ:</strong> {studentInfo.examCode}</p>
        <p><strong>Ph√≤ng thi:</strong> {studentInfo.roomName}</p>
      </div>

      {currentCode.answers.map((item, idx) => (
        <div className="question-box" key={idx}>
          <div className="question-title">C√¢u {idx + 1}: {item.question}</div>
          <div className="options">
            {["A", "B", "C", "D"].map((opt) => (
              <label key={opt}>
                <input
                  type="radio"
                  name={`q${idx}`}
                  value={opt}
                  checked={answers[idx] === opt}
                  onChange={() => handleAnswerChange(idx, opt)}
                />
                <span>{opt}</span>
              </label>
            ))}
          </div>
        </div>
      ))}

      <button onClick={handleSubmit} className="submit-button">N·ªôp b√†i</button>
    </div>
  );
}

export default QuizApp;
