import React, { useState } from "react";
import QuizApp from "./QuizApp";
import QuizForm from "./QuizForm";
import "./App.css";

function App() {
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [loginError, setLoginError] = useState("");
  const [startQuizForm, setStartQuizForm] = useState(false); // Bắt đầu tạo đề thi (QuizForm)

  const toggleLogin = () => {
    setShowLogin(!showLogin);
    setShowRegister(false);
  };

  const toggleRegister = () => {
    setShowRegister(!showRegister);
    setShowLogin(false);
  };

  const handleRegister = (e) => {
    e.preventDefault();
    const username = e.target.username.value;
    const password = e.target.password.value;
    const email = e.target.email.value;
    const role = e.target.role.value;
    localStorage.setItem("user", JSON.stringify({ username, password, email, role }));
    setShowRegister(false);
    alert("Đăng ký thành công!");
  };

  const handleLogin = (e) => {
    e.preventDefault();
    const username = e.target.username.value;
    const password = e.target.password.value;
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser && storedUser.username === username && storedUser.password === password) {
      setCurrentUser(storedUser);
      setLoginError("");
      setShowLogin(false);
    } else {
      setLoginError("Tên đăng nhập hoặc mật khẩu không đúng.");
    }
  };

  const handleLogout = () => {
    setCurrentUser(null);
    setStartQuizForm(false);
  };

  const closeModal = () => {
    setShowLogin(false);
    setShowRegister(false);
  };

  const handleGoHome = () => {
    window.location.href = "/";
  };

  const handleCreateQuiz = () => {
    if (!currentUser) {
      setShowLogin(true);
    } else if (currentUser.role !== "admin") {
      alert("Bạn phải là quản trị viên để tạo đề thi.");
    } else {
      setStartQuizForm(true);
    }
  };

  return (
    <div>
      <div className="navbar">
        <div className="navbar-left">
          <h2>QuizApp</h2>
        </div>
        <div className="navbar-right">
          <button className="nav-button" onClick={handleGoHome}>Trang chủ</button>
          {!currentUser ? (
            <>
              <button className="nav-button" onClick={toggleLogin}>Đăng nhập</button>
              <button className="nav-button" onClick={toggleRegister}>Đăng ký</button>
            </>
          ) : (
            <button className="nav-button" onClick={handleLogout}>Đăng xuất</button>
          )}
          <button className="nav-button" onClick={handleCreateQuiz}>Tạo đề thi</button>
          <button className="nav-button">Xem Kết Quả</button>
        </div>
      </div>

      {showLogin && (
        <div className="modal">
          <div className="form-box">
            <button className="close-btn" onClick={closeModal}>&times;</button>
            <h3>Đăng nhập</h3>
            <form onSubmit={handleLogin}>
              <input type="text" name="username" placeholder="Tên đăng nhập" required />
              <input type="password" name="password" placeholder="Mật khẩu" required />
              {loginError && <p style={{ color: "red" }}>{loginError}</p>}
              <button className="submit-button" type="submit">Đăng nhập</button>
            </form>
          </div>
        </div>
      )}

      {showRegister && (
        <div className="modal">
          <div className="form-box">
            <button className="close-btn" onClick={closeModal}>&times;</button>
            <h3>Đăng ký</h3>
            <form onSubmit={handleRegister}>
              <input type="text" name="username" placeholder="Tên người dùng" required />
              <input type="email" name="email" placeholder="Email" required />
              <input type="password" name="password" placeholder="Mật khẩu" required />
              <select name="role" required>
                <option value="user">Người dùng</option>
                <option value="admin">Quản trị viên</option>
              </select>
              <button className="submit-button" type="submit">Đăng ký</button>
            </form>
          </div>
        </div>
      )}

      {startQuizForm ? (
        <QuizForm numberOfQuestions={5} />
      ) : (
        <div className="welcome-container">
          <h1 className="welcome-title">Chào mừng đến với hệ thống thi trắc nghiệm</h1>
          <p className="welcome-text">Vui lòng đăng nhập hoặc đăng ký để bắt đầu.</p>
        </div>
      )}
    </div>
  );
}

export default App;
