import React, { useState } from "react";
import QuizApp from "./QuizApp";
import "./App.css"; // Import CSS thuần

function App() {
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [userData, setUserData] = useState({ username: "", password: "" });
  const [currentUser, setCurrentUser] = useState(null);
  const [loginError, setLoginError] = useState("");
  const [showQuizModal, setShowQuizModal] = useState(false); // Show modal for creating quiz
  const [numberOfQuestions, setNumberOfQuestions] = useState(1); // Default number of questions to 1

  // Form for login
  const toggleLogin = () => {
    setShowLogin(!showLogin);
    setShowRegister(false);
  };

  const toggleRegister = () => {
    setShowRegister(!showRegister);
    setShowLogin(false);
  };

  // Handle user registration
  const handleRegister = (e) => {
    e.preventDefault();
    const username = e.target.username.value;
    const password = e.target.password.value;
    const email = e.target.email.value;
    localStorage.setItem("user", JSON.stringify({ username, password, email }));
    setShowRegister(false);
    alert("Đăng ký thành công!");
  };

  // Handle user login
  const handleLogin = (e) => {
    e.preventDefault();
    const username = e.target.username.value;
    const password = e.target.password.value;
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (
      storedUser &&
      storedUser.username === username &&
      storedUser.password === password
    ) {
      setCurrentUser(storedUser);
      setLoginError("");
      setShowLogin(false);
    } else {
      setLoginError("Tên đăng nhập hoặc mật khẩu không đúng.");
    }
  };

  // Handle logout
  const handleLogout = () => {
    setCurrentUser(null);
  };

  // Close Modal (Login/Register)
  const closeModal = () => {
    setShowLogin(false);
    setShowRegister(false);
  };

  // Handle Go Home
  const handleGoHome = () => {
    window.location.href = "/";
  };

  // Handle creating the quiz and selecting number of questions
  const handleCreateQuiz = () => {
    setShowQuizModal(true); // Show the modal to select number of questions
  };

  // Handle number of questions selection
  const handleQuestionSelection = (num) => {
    setNumberOfQuestions(num);
    setShowQuizModal(false); // Close the modal
  };

  return (
    <div>
      <div className="navbar">
        <div className="navbar-left">
          <h2>Quizzz</h2>
        </div>
        <div className="navbar-right">
          <button className="nav-button" onClick={handleGoHome}>
            Trang chủ
          </button>
          {!currentUser ? (
            <>
              <button className="nav-button" onClick={toggleLogin}>
                Đăng nhập
              </button>
              <button className="nav-button" onClick={toggleRegister}>
                Đăng ký
              </button>
            </>
          ) : (
            <>
              <button className="nav-button" onClick={handleLogout}>
                Đăng xuất
              </button>
            </>
          )}
          <button className="nav-button" onClick={handleCreateQuiz}>
            Tạo đề thi
          </button>
          <button className="nav-button">Xem Kết Quả</button>
        </div>
      </div>

      {/* Login Modal */}
      {showLogin && (
        <div className="modal">
          <div className="form-box">
            <button className="close-btn" onClick={closeModal}>
              &times;
            </button>
            <h3>Đăng nhập</h3>
            <form onSubmit={handleLogin}>
              <input
                type="text"
                name="username"
                placeholder="Tên đăng nhập"
                required
              />
              <input
                type="password"
                name="password"
                placeholder="Mật khẩu"
                required
              />
              {loginError && <p style={{ color: "red" }}>{loginError}</p>}
              <button className="submit-button" type="submit">
                Đăng nhập
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Register Modal */}
      {showRegister && (
        <div className="modal">
          <div className="form-box">
            <button className="close-btn" onClick={closeModal}>
              &times;
            </button>
            <h3>Đăng ký</h3>
            <form onSubmit={handleRegister}>
              <input
                type="text"
                name="username"
                placeholder="Tên người dùng"
                required
              />
              <input type="email" name="email" placeholder="Email" required />
              <input
                type="password"
                name="password"
                placeholder="Mật khẩu"
                required
              />
              <button className="submit-button" type="submit">
                Đăng ký
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Quiz Creation Modal */}
      {showQuizModal && (
        <div className="modal">
          <div className="form-box">
            <button
              className="close-btn"
              onClick={() => setShowQuizModal(false)}
            >
              &times;
            </button>
            <h3>Chọn số câu hỏi</h3>
            <div className="question-options">
              {[...Array(10).keys()].map((num) => (
                <button
                  key={num + 1}
                  className="question-button"
                  onClick={() => handleQuestionSelection(num + 1)}
                >
                  {num + 1} Câu
                </button>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Quiz Container */}
      {currentUser && numberOfQuestions && (
        <div className="quiz-container">
          <h1 className="quiz-title">Thi Giữa Kì - năm học 2025-2026</h1>
          <h2 className="quiz-subtitle">
            Môn: Xây Dựng Phần Mềm Web - Thời gian: 30 phút
          </h2>
          <p className="quiz-warning">Lưu ý: Không sử dụng tài liệu</p>
          <QuizApp numQuestions={numberOfQuestions} />
        </div>
      )}
    </div>
  );
}

export default App;
