import React, { useState } from "react";
import QuizApp from "./QuizApp"; // Đảm bảo bạn import đúng component

function App() {
  const [currentPage, setCurrentPage] = useState("createRoom"); // Trang ban đầu là tạo phòng thi

  // Xử lý khi tạo phòng thi và vào phòng thi
  const handleCreateRoom = () => {
    setCurrentPage("quizApp");
  };

  // Xử lý khi nhấn nút "Thi lại"
  const handleRetry = () => {
    setCurrentPage("createRoom"); // Khi nhấn "Thi lại", quay lại trang tạo phòng thi
  };

  return (
    <div>
      {/* Trang Tạo Phòng Thi */}
      {currentPage === "createRoom" && (
        <div>
          <h1>Tạo phòng thi</h1>
          {/* Bạn có thể thêm giao diện để tạo phòng thi ở đây */}
          <button onClick={handleCreateRoom} className="bg-blue-500 text-white p-2 mt-4 rounded">
            Vào phòng thi
          </button>
        </div>
      )}

      {/* Trang Làm Bài Thi */}
      {currentPage === "quizApp" && (
        <QuizApp onRetry={handleRetry} />
      )}
    </div>
  );
}

export default App;
