import React, { useEffect, useState } from "react";
import "./QuizApp.css";

function QuizApp({ examConfig, studentInfo, currentUser, onRetry }) {
  const [timeLeft, setTimeLeft] = useState(30 * 60);
  const [isExamOver, setIsExamOver] = useState(false);
  const [studentAnswers, setStudentAnswers] = useState([]);
  const [score, setScore] = useState(null);
  const [isPassed, setIsPassed] = useState(null);

  useEffect(() => {
    if (currentUser?.role === "admin") return; // Nếu là admin thì không set timer
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => {
        if (prevTime <= 0) {
          clearInterval(timer);
          handleSubmit();
          return 0;
        }
        return prevTime - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  // ❌ Không cho admin làm bài thi
  if (currentUser?.role === "admin") {
    return (
      <div className="center-screen">
        <div className="form-box">
          <h2>Không thể bắt đầu thi</h2>
          <p style={{ color: "red" }}>
            Tài khoản quản trị viên không được phép làm bài thi.
          </p>
        </div>
      </div>
    );
  }

  if (!studentInfo || !examConfig) return <p>Đang tải dữ liệu...</p>;

  const exam = examConfig.examCodes.find(
    (e) => e.code === studentInfo.examCode
  );

  if (!exam || !exam.questionsData)
    return <p>Không tìm thấy đề thi phù hợp!</p>;

  const handleAnswerChange = (questionIndex, answerIndex) => {
    const newAnswers = [...studentAnswers];
    newAnswers[questionIndex] = answerIndex;
    setStudentAnswers(newAnswers);
  };

  const handleSubmit = () => {
    setIsExamOver(true);
    let correctCount = 0;
    exam.questionsData.forEach((q, index) => {
      if (studentAnswers[index] === q.correctAnswer) {
        correctCount++;
      }
    });

    const total = exam.questionsData.length;
    const passed = correctCount >= Math.ceil(total / 2);

    setScore(correctCount);
    setIsPassed(passed);
    localStorage.setItem("studentAnswers", JSON.stringify(studentAnswers));
  };

  const handleRetry = () => {
    setIsExamOver(false);
    setScore(null);
    setIsPassed(null);
    setStudentAnswers([]);
    if (onRetry) onRetry();
  };

  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    return `${minutes}:${seconds < 10 ? `0${seconds}` : seconds}`;
  };

  return (
    <div className="quiz-container">
      <h2>
        Đề {studentInfo.examCode} - Phòng {studentInfo.roomName}
      </h2>
      <p>
        Họ tên: {studentInfo.studentName} - MSSV: {studentInfo.studentId}
      </p>
      <p>Số câu hỏi: {exam.questionsData.length}</p>

      <div className="timer">
        <h4>Thời gian còn lại: {formatTime(timeLeft)}</h4>
        {isExamOver && <p>Thời gian đã hết!</p>}
      </div>

      {exam.questionsData.map((q, index) => (
        <div key={index} className="question-box">
          <h4>
            Câu {index + 1}: {q.questionText}
          </h4>
          {q.options.map((opt, i) => (
            <div key={i}>
              <label>
                <input
                  type="radio"
                  name={`q${index}`}
                  value={i}
                  checked={studentAnswers[index] === i}
                  onChange={() => handleAnswerChange(index, i)}
                  disabled={isExamOver}
                />
                {String.fromCharCode(65 + i)}. {opt}
              </label>
            </div>
          ))}
        </div>
      ))}

      {!isExamOver && (
        <button
          className="submit-button bg-blue-500 text-white p-2 mt-4 rounded"
          onClick={handleSubmit}
        >
          Nộp bài
        </button>
      )}

      {isExamOver && score !== null && (
        <div className="result mt-4">
          <h3>
            Kết quả:{" "}
            <span style={{ color: isPassed ? "green" : "red" }}>
              {isPassed ? "ĐẠT" : "KHÔNG ĐẠT"}
            </span>{" "}
            ({score}/{exam.questionsData.length} câu đúng)
          </h3>
          <button
            onClick={handleRetry}
            className="retry-button bg-yellow-500 text-white p-2 mt-2 rounded"
          >
            Thi lại
          </button>
        </div>
      )}
    </div>
  );
}

export default QuizApp;
