import React, { useState } from "react";
import LoginModal from "./components/LoginModal";
import RegisterModal from "./components/RegisterModal";
import QuizModal from "./components/QuizModal";
import Navbar from "./components/Navbar";
import QuizApp from "./components/QuizApp";
import "./styles/App.css"; // Import CSS thuần

function App() {
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [showQuizModal, setShowQuizModal] = useState(false); // Show modal for creating quiz
  const [numberOfQuestions, setNumberOfQuestions] = useState(1); // Default number of questions to 1
  const [successMessage, setSuccessMessage] = useState(""); // State to hold success message
  const [startQuiz, setStartQuiz] = useState(false); // State to start the quiz

  // Handle user login
  const handleLogin = (userData) => {
    setCurrentUser(userData);
    setShowLogin(false);
  };

  // Handle user logout
  const handleLogout = () => {
    setCurrentUser(null);
  };

  // Handle quiz modal opening (Admin only can create quizzes)
  const handleCreateQuiz = () => {
    if (!currentUser) {
      setShowLogin(true); // If not logged in, show login modal
    } else if (currentUser.role !== "admin") {
      alert("Bạn không có quyền tạo đề thi!");
    } else {
      setShowQuizModal(true); // If admin, allow quiz creation
    }
  };

  return (
    <div>
      <Navbar
        currentUser={currentUser}
        handleLogout={handleLogout}
        handleCreateQuiz={handleCreateQuiz}
        setShowLogin={setShowLogin}
        setShowRegister={setShowRegister}
      />

      <LoginModal
        showLogin={showLogin}
        setShowLogin={setShowLogin}
        handleLogin={handleLogin}
      />

      <RegisterModal
        showRegister={showRegister}
        setShowRegister={setShowRegister}
      />

      <QuizModal
        showQuizModal={showQuizModal}
        setShowQuizModal={setShowQuizModal}
        setNumberOfQuestions={setNumberOfQuestions}
        setSuccessMessage={setSuccessMessage}
        setStartQuiz={setStartQuiz}
      />

      {/* Success Message */}
      {successMessage && (
        <div className="success-message">{successMessage}</div>
      )}

      {/* Show QuizApp Component if quiz has started */}
      {startQuiz && numberOfQuestions && (
        <div className="quiz-container">
          <h1 className="quiz-title">Thi Giữa Kỳ - năm học 2025-2026</h1>
          <h2 className="quiz-subtitle">
            Môn: Xây Dựng Phần Mềm Web - Thời gian: 30 phút
          </h2>
          <p className="quiz-warning">Lưu ý: Không sử dụng tài liệu</p>
          <QuizApp numQuestions={numberOfQuestions} />
        </div>
      )}
    </div>
  );
}

export default App;
