import React from "react";

export default function QuestionInput({ index, selected, onSelect }) {
  return (
    <div className="space-y-2">
      <label className="block font-medium">CÃ¢u {index + 1}</label>
      <div className="flex gap-4">
        {["A", "B", "C", "D"].map((choice) => (
          <label key={choice} className="flex items-center gap-1">
            <input
              type="radio"
              name={`q${index}`}
              value={choice}
              checked={selected === choice}
              onChange={() => onSelect(index, choice)}
            />
            {choice}
          </label>
        ))}
      </div>
    </div>
  );
}
