import React, { useState } from "react";
import { Card, CardContent } from "./ui/card";
import { Button } from "./ui/button";
import { Input } from "./ui/input";

export default function QuizForm({ numberOfQuestions }) {
  const [selected, setSelected] = useState(Array(numberOfQuestions).fill(""));

  const handleSelect = (qIndex, value) => {
    const newSelected = [...selected];
    newSelected[qIndex] = value;
    setSelected(newSelected);
  };

  const handleSubmit = () => {
    alert("Đã lưu đề thi!");
    console.log("Đáp án đã chọn:", selected);
  };

  return (
    <div className="p-6 space-y-4">
      <Card>
        <CardContent className="p-6 space-y-4">
          <h2 className="text-xl font-semibold">Tạo đề thi</h2>
          <Input placeholder="Tên đề thi" />

          <div className="grid grid-cols-2 gap-4">
            <select className="border p-2 rounded">
              <option>---Chọn khối---</option>
              <option>Khối 10</option>
              <option>Khối 11</option>
              <option>Khối 12</option>
            </select>
            <select className="border p-2 rounded">
              <option>---Chọn môn---</option>
              <option>Toán</option>
              <option>Lý</option>
              <option>Hóa</option>
            </select>
          </div>

          {[...Array(numberOfQuestions)].map((_, qIndex) => (
            <div key={qIndex} className="space-y-2">
              <label>Câu {qIndex + 1}</label>
              <div className="flex gap-4">
                {["A", "B", "C", "D"].map((choice) => (
                  <label key={choice}>
                    <input
                      type="radio"
                      name={`q${qIndex}`}
                      value={choice}
                      checked={selected[qIndex] === choice}
                      onChange={() => handleSelect(qIndex, choice)}
                    />
                    {choice}
                  </label>
                ))}
              </div>
            </div>
          ))}

          <Button onClick={handleSubmit} className="mt-4">Lưu</Button>
        </CardContent>
      </Card>
    </div>
  );
}
