import React, { useState } from "react";
import QuizApp from "./QuizApp";
import "./App.css"; // Import CSS thuần

function App() {
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);

  const toggleLogin = () => {
    setShowLogin(!showLogin);
    setShowRegister(false); // chỉ hiện 1 form tại 1 thời điểm
  };

  const toggleRegister = () => {
    setShowRegister(!showRegister);
    setShowLogin(false);
  };

  return (
    <div>
      <div className="navbar">
        <div className="navbar-left">
          <h2>Quizzz</h2>
        </div>
        <div className="navbar-right">
          <button className="nav-button" onClick={toggleLogin}>
            Đăng nhập
          </button>
          <button className="nav-button" onClick={toggleRegister}>
            Đăng ký
          </button>
          <button className="nav-button">Đăng xuất</button>
          <button className="nav-button">Tạo đề thi</button>
          <button className="nav-button">Xem Kết Quả</button>
        </div>
      </div>

      {showLogin && (
        <div className="modal">
          <div className="form-box">
            <h3>Đăng nhập</h3>
            <input type="text" placeholder="Tên đăng nhập" />
            <input type="password" placeholder="Mật khẩu" />
            <button className="submit-button">Đăng nhập</button>
          </div>
        </div>
      )}

      {showRegister && (
        <div className="modal">
          <div className="form-box">
            <h3>Đăng ký</h3>
            <input type="text" placeholder="Tên người dùng" />
            <input type="email" placeholder="Email" />
            <input type="password" placeholder="Mật khẩu" />
            <button className="submit-button">Đăng ký</button>
          </div>
        </div>
      )}

      <div className="quiz-container">
        <h1 className="quiz-title">Thi Giữa Kì - năm học 2025-2026</h1>
        <h2 className="quiz-subtitle">
          Môn: Xây Dựng Phần Mềm Web - Thời gian: 30 phút
        </h2>
        <p className="quiz-warning">Lưu ý: Không sử dụng tài liệu</p>
        <QuizApp />
      </div>
    </div>
  );
}

export default App;
