import React, { useState, useEffect } from "react";

function App() {
  const [studentInfo, setStudentInfo] = useState({
    studentName: "Nguy·ªÖn VƒÉn A",
    studentId: "123456789",
    roomName: "Ph√≤ng thi 1",
