import React, { useState, useEffect } from "react";

function App() {
  const [studentInfo, setStudentInfo] = useState({
    studentName: "Nguyễn Văn A",
    studentId: "123456789",
    roomName: "Phòng thi 1",
    examCode: "exam001",
  });

  const [examConfig, setExamConfig] = useState({
    examCodes: [
      {
        id: "exam001",
        questions: [
          {
            questionText: "Câu 1: 1 + 1 = ?",
            correctAnswer: 1, // B: 2
          },
          {
            questionText: "Câu 2: 3 + 3 = ?",
            correctAnswer: 0, // A: 6
          },
          {
            questionText: "Câu 3: 2 + 2 = ?",
            correctAnswer: 2, // C: 4
          },
        ],
      },
    ],
  });

  const [studentAnswers, setStudentAnswers] = useState([1, 0, 2]); // Đáp án học sinh chọn: B, A, C

  const [result, setResult] = useState(null);

  useEffect(() => {
    // Giả lập tính điểm khi dữ liệu đã có sẵn
    const exam = examConfig.examCodes.find(
      (e) => e.id === studentInfo.examCode
    );
    let score = 0;
    const details = exam.questions.map((q, idx) => {
      const selected = studentAnswers[idx];
      const isCorrect = q.correctAnswer === selected;
      if (isCorrect) score++;

      return {
        question: q.questionText,
        correctAnswer: String.fromCharCode(65 + q.correctAnswer), // Chuyển đổi đáp án từ 0, 1, 2 thành A, B, C
        selectedAnswer: selected !== undefined ? String.fromCharCode(65 + selected) : "Chưa chọn",
        isCorrect,
      };
    });

    const resultData = {
      details,
      score,
      total: exam.questions.length,
      status: score >= exam.questions.length / 2 ? "Đạt" : "Không đạt",
    };

    setResult(resultData);
  }, [studentAnswers, examConfig, studentInfo]);

  return (
    <div>
      <h3>📋 Kết quả làm bài</h3>
      {result && (
        <div>
          <p>
            <strong>Họ tên:</strong> {studentInfo.studentName}
          </p>
          <p>
            <strong>MSSV:</strong> {studentInfo.studentId}
          </p>
          <p>
            <strong>Phòng thi:</strong> {studentInfo.roomName}
          </p>
          <p>
            <strong>Mã đề:</strong> {studentInfo.examCode}
          </p>

          <h4>Chi tiết bài làm:</h4>
          {result.details.map((item, idx) => (
            <div key={idx} style={{ marginBottom: 10 }}>
              <p><strong>{item.question}</strong></p>
              <p><strong>✅ Đáp án đúng:</strong> {item.correctAnswer}</p>
              <p><strong>📝 Bạn chọn:</strong> {item.selectedAnswer}</p>
              <p>{item.isCorrect ? "✅ Đúng" : "❌ Sai"}</p>
              <hr />
            </div>
          ))}

          <p>
            <strong>🎯 Tổng điểm:</strong> {result.score} / {result.total}
          </p>
          <p>
            <strong>Trạng thái:</strong> {result.status}
          </p>
        </div>
      )}
    </div>
  );
}

export default App;
