import React, { useEffect, useState } from "react";

function QuizApp({ studentInfo, examConfig, setStudentAnswers, goToResult }) {
  const [selectedAnswers, setSelectedAnswers] = useState([]);

  const exam = examConfig.examCodes.find(
    (e) => e.code === studentInfo.examCode
  );

  useEffect(() => {
    if (exam) {
      setSelectedAnswers(Array(exam.questionsData.length).fill(null));
    }
  }, [exam]);

  const handleOptionChange = (qIndex, optIndex) => {
    const updated = [...selectedAnswers];
    updated[qIndex] = optIndex;
    setSelectedAnswers(updated);
  };

  const handleSubmit = () => {
    setStudentAnswers(selectedAnswers);
    goToResult();
  };

  if (!exam || !exam.questionsData) return <p>Không tìm thấy đề thi phù hợp!</p>;

  return (
    <div className="quiz-container">
      <h2>
        Đề {studentInfo.examCode} - Phòng {studentInfo.roomName}
      </h2>
      <p>
        Họ tên: {studentInfo.studentName} - MSSV: {studentInfo.studentId}
      </p>
      <p>
        Số câu hỏi: {exam.questionsData.length}
      </p>

      {exam.questionsData.map((q, index) => (
        <div key={index} className="question-box">
          <h4>
            Câu {index + 1}: {q.questionText}
          </h4>
          {q.options.map((opt, i) => (
            <div key={i}>
              <label>
                <input
                  type="radio"
                  name={`q${index}`}
                  value={i}
                  checked={selectedAnswers[index] === i}
                  onChange={() => handleOptionChange(index, i)}
                />
                {String.fromCharCode(65 + i)}. {opt}
              </label>
            </div>
          ))}
        </div>
      ))}

      <button onClick={handleSubmit} className="button-black">Nộp bài</button>
    </div>
  );
}

export default QuizApp;
