import React, { useState } from "react";
import QuizApp from "./QuizApp";
import CreateExamForm from "./CreateExamForm";
import CreateForm from "./CreateForm";
import "./App.css";

function App() {
  const [currentPage, setCurrentPage] = useState("home");
  const [currentUser, setCurrentUser] = useState(null);
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [loginError, setLoginError] = useState("");

  const toggleLogin = () => {
    setShowLogin(!showLogin);
    setShowRegister(false);
  };

  const toggleRegister = () => {
    setShowRegister(!showRegister);
    setShowLogin(false);
  };

  const handleLogin = (e) => {
    e.preventDefault();
    const username = e.target.username.value;
    const password = e.target.password.value;
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (
      storedUser &&
      storedUser.username === username &&
      storedUser.password === password
    ) {
      setCurrentUser(storedUser);
      setLoginError("");
      setShowLogin(false);
    } else {
      setLoginError("Tên đăng nhập hoặc mật khẩu không đúng.");
    }
  };

  const handleRegister = (e) => {
    e.preventDefault();
    const user = {
      username: e.target.username.value,
      password: e.target.password.value,
      email: e.target.email.value,
      role: e.target.role.value,
    };
    localStorage.setItem("user", JSON.stringify(user));
    alert("Đăng ký thành công!");
    setShowRegister(false);
  };

  const handleLogout = () => {
    setCurrentUser(null);
    setCurrentPage("home");
  };

  const handleCreateQuiz = () => {
    if (!currentUser) {
      setShowLogin(true);
    } else if (currentUser.role !== "admin") {
      alert("Bạn phải là quản trị viên để tạo đề thi.");
    } else {
      setCurrentPage("createExam");
    }
  };

  return (
    <div>
      {/* Navbar */}
      <div className="navbar">
        <div className="navbar-left">
          <h2>QuizApp</h2>
        </div>
        <div className="navbar-right">
          <button className="nav-button" onClick={() => setCurrentPage("home")}>
            Trang chủ
          </button>
          <button
            className="nav-button"
            onClick={() => setCurrentPage("result")}
          >
            Xem kết quả
          </button>
          {!currentUser ? (
            <>
              <button className="nav-button" onClick={toggleLogin}>
                Đăng nhập
              </button>
              <button className="nav-button" onClick={toggleRegister}>
                Đăng ký
              </button>
            </>
          ) : (
            <button className="nav-button" onClick={handleLogout}>
              Đăng xuất
            </button>
          )}
          <button className="nav-button" onClick={handleCreateQuiz}>
            Tạo đề thi
          </button>
        </div>
      </div>

      {/* Modal Đăng nhập / Đăng ký */}
      {showLogin && (
        <div className="modal">
          <div className="form-box">
            <button className="close-btn" onClick={() => setShowLogin(false)}>
              &times;
            </button>
            <h3>Đăng nhập</h3>
            <form onSubmit={handleLogin}>
              <input
                type="text"
                name="username"
                placeholder="Tên đăng nhập"
                required
              />
              <input
                type="password"
                name="password"
                placeholder="Mật khẩu"
                required
              />
              {loginError && <p style={{ color: "red" }}>{loginError}</p>}
              <button type="submit" className="submit-button">
                Đăng nhập
              </button>
            </form>
          </div>
        </div>
      )}
      {showRegister && (
        <div className="modal">
          <div className="form-box">
            <button
              className="close-btn"
              onClick={() => setShowRegister(false)}
            >
              &times;
            </button>
            <h3>Đăng ký</h3>
            <form onSubmit={handleRegister}>
              <input name="username" placeholder="Tên người dùng" required />
              <input name="email" type="email" placeholder="Email" required />
              <input
                name="password"
                type="password"
                placeholder="Mật khẩu"
                required
              />
              <select name="role" required>
                <option value="user">Người dùng</option>
                <option value="admin">Quản trị viên</option>
              </select>
              <button className="submit-button">Đăng ký</button>
            </form>
          </div>
        </div>
      )}

      {/* Điều hướng giao diện */}
      {currentPage === "home" && <div style={{ padding: 20 }}></div>}
      {currentPage === "createExam" && (
        <CreateExamForm goToCreateForm={() => setCurrentPage("createForm")} />
      )}
      {currentPage === "createForm" && <CreateForm />}
      {currentPage === "result" && (
        <div style={{ padding: 20 }}>
          <h3>Kết quả làm bài (placeholder)</h3>
          {/* Bạn có thể thay thế đoạn này bằng component hiển thị điểm và lịch sử */}
        </div>
      )}
    </div>
  );
}

export default App;
