import React, { useState } from "react";
import "./QuizApp.css"; // Đảm bảo file CSS này tồn tại cùng thư mục

function CreateExamForm({ setExamConfig, goToCreateForm }) {
  const [examName, setExamName] = useState("");
  const [grade, setGrade] = useState("");
  const [subject, setSubject] = useState("");
  const [scoring, setScoring] = useState({
    correct1: "",
    correct2: "",
    correct3: "",
    correct4: "",
  });

  const [examCodes, setExamCodes] = useState([
    {
      id: 1,
      questions: 5,
      totalScore: 8,
      answers: Array(1).fill().map(() => ({
        question: "",
        options: { A: "", B: "", C: "", D: "" },
        correct: "A",
      })),
    },
  ]);

  const handleQuestionCountChange = (index, value) => {
    const updated = [...examCodes];
    const num = parseInt(value);
    updated[index].questions = num;
    updated[index].answers = Array(num).fill({
      question: "",
      options: { A: "", B: "", C: "", D: "" },
      correct: "A",
    });
    setExamCodes(updated);
  };

  const handleTotalScoreChange = (index, value) => {
    const updated = [...examCodes];
    updated[index].totalScore = parseFloat(value);
    setExamCodes(updated);
  };

  const handleQuestionTextChange = (examIndex, qIndex, value) => {
    const updated = [...examCodes];
    updated[examIndex].answers[qIndex].question = value;
    setExamCodes(updated);
  };

  const handleOptionChange = (examIndex, qIndex, option, value) => {
    const updated = [...examCodes];
    updated[examIndex].answers[qIndex].options[option] = value;
    setExamCodes(updated);
  };

  const handleCorrectAnswerChange = (examIndex, qIndex, value) => {
    const updated = [...examCodes];
    updated[examIndex].answers[qIndex].correct = value;
    setExamCodes(updated);
  };

  const addExamCode = () => {
    const newId = examCodes.length + 1;
    const newCode = {
      id: newId,
      questions: 5,
      totalScore: 10,
      answers: Array(5).fill({
        question: "",
        options: { A: "", B: "", C: "", D: "" },
        correct: "A",
      }),
    };
    setExamCodes([...examCodes, newCode]);
  };

  const calculateScorePerQuestion = (code) => {
    return (code.totalScore / code.questions).toFixed(2);
  };

  const handleSave = () => {
    const examData = {
      examName,
      grade,
      subject,
      scoring,
      examCodes,
    };

    setExamConfig(examData); // Truyền dữ liệu về App.js
    goToCreateForm(); // Điều hướng sang CreateForm

    console.log("Đề thi đã được lưu:", examData);
  };

  const handleCancel = () => {
    window.location.href = "/";
  };

  return (
    <div className="create-exam-container">
      <h2 className="form-title">Tạo đề thi</h2>

      <div className="form-group">
        <label>Tên đề:</label>
        <input
          className="form-input"
          value={examName}
          onChange={(e) => setExamName(e.target.value)}
          placeholder="Nhập tên đề..."
        />
      </div>

      <div className="form-group-row">
        <div>
          <label>Khối học:</label>
          <input
            className="form-input"
            value={grade}
            onChange={(e) => setGrade(e.target.value)}
            placeholder="Nhập khối học..."
          />
        </div>
        <div>
          <label>Môn học:</label>
          <input
            className="form-input"
            value={subject}
            onChange={(e) => setSubject(e.target.value)}
            placeholder="Nhập môn học..."
          />
        </div>
      </div>

      <h4 className="form-subtitle">Danh sách mã đề</h4>
      {examCodes.map((code, index) => (
        <div key={index} className="exam-code-box">
          <div className="form-group-row">
            <label>
              Số lượng câu hỏi:
              <input
                type="number"
                min="1"
                className="form-input"
                value={code.questions}
                onChange={(e) =>
                  handleQuestionCountChange(index, e.target.value)
                }
              />
            </label>
            <label>
              Tổng điểm:
              <input
                type="number"
                min="0"
                className="form-input"
                value={code.totalScore}
                onChange={(e) => handleTotalScoreChange(index, e.target.value)}
              />
            </label>
            <span className="score-per-question">
              Mỗi câu: {calculateScorePerQuestion(code)} điểm
            </span>
          </div>

          {/* Nhập các câu hỏi và lựa chọn đáp án */}
          {code.answers.map((answer, qIndex) => (
            <div key={qIndex} className="question-box">
              <label>Câu hỏi {qIndex + 1}:</label>
              <input
                className="form-input"
                value={answer.question}
                onChange={(e) =>
                  handleQuestionTextChange(index, qIndex, e.target.value)
                }
                placeholder="Nhập câu hỏi..."
              />

              {Object.keys(answer.options).map((optionKey) => (
                <div key={optionKey}>
                  <label>{optionKey}:</label>
                  <input
                    className="form-input"
                    value={answer.options[optionKey]}
                    onChange={(e) =>
                      handleOptionChange(
                        index,
                        qIndex,
                        optionKey,
                        e.target.value
                      )
                    }
                    placeholder={`Nhập đáp án ${optionKey}`}
                  />
                </div>
              ))}

              <div>
                <label>Đáp án đúng:</label>
                <select
                  className="form-input"
                  value={answer.correct}
                  onChange={(e) =>
                    handleCorrectAnswerChange(index, qIndex, e.target.value)
                  }
                >
                  {["A", "B", "C", "D"].map((option) => (
                    <option key={option} value={option}>
                      {option}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          ))}
        </div>
      ))}

      <button onClick={addExamCode} className="button-green">
        + Tạo Mã Đề Mới
      </button>

      <div className="form-group-row">
        <button onClick={handleSave} className="button-black">
          Lưu
        </button>
        <button onClick={handleCancel} className="button-gray">
          Hủy
        </button>
      </div>
    </div>
  );
}

export default CreateExamForm;
