import React, { useEffect, useState } from "react";

function QuizApp({ onRetry }) {
  const [studentData, setStudentData] = useState(null);
  const [examConfig, setExamConfig] = useState(null);
  const [timeLeft, setTimeLeft] = useState(30 * 60);
  const [isExamOver, setIsExamOver] = useState(false);
  const [studentAnswers, setStudentAnswers] = useState([]);
  const [score, setScore] = useState(null);
  const [isPassed, setIsPassed] = useState(null);

  useEffect(() => {
    const storedStudent = JSON.parse(localStorage.getItem("studentData"));
    const storedExam = JSON.parse(localStorage.getItem("examConfig"));
    setStudentData(storedStudent);
    setExamConfig(storedExam);

    const timer = setInterval(() => {
      setTimeLeft((prevTime) => {
        if (prevTime <= 0) {
          clearInterval(timer);
          handleSubmit(); // Tự động nộp khi hết giờ
          return 0;
        }
        return prevTime - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  const handleAnswerChange = (questionIndex, answerIndex) => {
    const newAnswers = [...studentAnswers];
    newAnswers[questionIndex] = answerIndex;
    setStudentAnswers(newAnswers);
  };

  const handleSubmit = () => {
    setIsExamOver(true);
    const exam = examConfig.examCodes.find(
      (e) => e.code === studentData.examCode
    );

    let correctCount = 0;
    exam.questionsData.forEach((q, index) => {
      if (studentAnswers[index] === q.correctAnswer) {
        correctCount++;
      }
    });

    const total = exam.questionsData.length;
    const passed = correctCount >= Math.ceil(total / 2);

    setScore(correctCount);
    setIsPassed(passed);
    localStorage.setItem("studentAnswers", JSON.stringify(studentAnswers));
  };

  const handleRetry = () => {
    setIsExamOver(false);
    setScore(null);
    setIsPassed(null);
    setStudentAnswers([]);
    if (onRetry) onRetry(); // quay về bước tạo phòng thi
  };

  const dummyExamConfig = {
    examCodes: [
      {
        code: "EX001",
        questionsData: [
          {
            questionText: "React là gì?",
            options: ["Thư viện JS", "Ngôn ngữ lập trình", "Trình soạn thảo", "Hệ điều hành"],
            correctAnswer: 0
          },
          {
            questionText: "JSX là viết tắt của?",
            options: ["Java Syntax Extension", "JavaScript XML", "JS Express", "Java Structured XML"],
            correctAnswer: 1
          },
          {
            questionText: "Hook nào dùng để quản lý state trong React?",
            options: ["useProps", "useStore", "useEffect", "useState"],
            correctAnswer: 3
          },
          {
            questionText: "Thành phần nào không thuộc React?",
            options: ["Component", "Fragment", "Router", "Middleware"],
            correctAnswer: 3
          },
          {
            questionText: "Câu lệnh nào dùng để import React?",
            options: ["require('react')", "import React from 'react'", "include React", "use React"],
            correctAnswer: 1
          }
        ]
      }
    ]
  };
  
  localStorage.setItem("examConfig", JSON.stringify(dummyExamConfig));

  if (!exam || !exam.questionsData)
    return <p>Không tìm thấy đề thi phù hợp!</p>;

  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    return `${minutes}:${seconds < 10 ? `0${seconds}` : seconds}`;
  };

  return (
    <div className="quiz-container">
      <h2>
        Đề {studentData.examCode} - Phòng {studentData.roomName}
      </h2>
      <p>
        Họ tên: {studentData.studentName} - MSSV: {studentData.studentId}
      </p>
      <p>Số câu hỏi: {exam.questionsData.length}</p>

      <div className="timer">
        <h4>Thời gian còn lại: {formatTime(timeLeft)}</h4>
        {isExamOver && <p>Thời gian đã hết!</p>}
      </div>

      {exam.questionsData.map((q, index) => (
        <div key={index} className="question-box">
          <h4>
            Câu {index + 1}: {q.questionText}
          </h4>
          {q.options.map((opt, i) => (
            <div key={i}>
              <label>
                <input
                  type="radio"
                  name={`q${index}`}
                  value={i}
                  checked={studentAnswers[index] === i}
                  onChange={() => handleAnswerChange(index, i)}
                  disabled={isExamOver}
                />
                {String.fromCharCode(65 + i)}. {opt}
              </label>
            </div>
          ))}
        </div>
      ))}

      {!isExamOver && (
        <button className="submit-button bg-blue-500 text-white p-2 mt-4 rounded" onClick={handleSubmit}>
          Nộp bài
        </button>
      )}

      {isExamOver && score !== null && (
        <div className="result mt-4">
          <h3>
            Kết quả:{" "}
            <span style={{ color: isPassed ? "green" : "red" }}>
              {isPassed ? "ĐẠT" : "KHÔNG ĐẠT"}
            </span>{" "}
            ({score}/{exam.questionsData.length} câu đúng)
          </h3>
          <button
            onClick={handleRetry}
            className="retry-button bg-yellow-500 text-white p-2 mt-2 rounded"
          >
            Thi lại
          </button>
        </div>
      )}
    </div>
  );
}

export default QuizApp;
