import React, { useEffect, useState } from "react";

function QuizApp({ onRetry, onCreateNewRoom }) {
  const [studentData, setStudentData] = useState({
    studentName: "Nguyễn Văn A",
    studentId: "123456789",
    examCode: "EX123",
    roomName: "Phòng 1",
  });

  const [examConfig, setExamConfig] = useState({
    examCodes: [
      {
        code: "EX123",
        questionsData: [
          {
            questionText: "Câu hỏi 1: question la cai cho gi = ?",
            options: [
              "mot cai gi do rat xam lz",
              "dung hoi nưa khong muon biet",
              "len chatgpt di choi ",
              "hahahahahahaha"
            ],
            correctAnswer: 1,
          },
          {
            questionText: "Câu hỏi 2: 3 + 5 = ?",
            options: ["7", "8", "9", "10"],
            correctAnswer: 1,
          },
          {
            questionText: "Câu hỏi 3: 5 + 7 = ?",
            options: ["11", "12", "13", "14"],
            correctAnswer: 1,
          },
        ],
      },
    ],
  });

  const [timeLeft, setTimeLeft] = useState(30 * 60); // 30 phút
  const [isExamOver, setIsExamOver] = useState(false);
  const [studentAnswers, setStudentAnswers] = useState([]);
  const [score, setScore] = useState(null);
  const [isPassed, setIsPassed] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false); // State để quản lý menu

  // Tạo một effect để xử lý đồng hồ đếm ngược
  useEffect(() => {
    if (isExamOver) return; // Không chạy timer nếu đã hết giờ
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => {
        if (prevTime <= 0) {
          clearInterval(timer);
          handleSubmit();
          return 0;
        }
        return prevTime - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [isExamOver]);

  const handleAnswerChange = (questionIndex, answerIndex) => {
    const newAnswers = [...studentAnswers];
    newAnswers[questionIndex] = answerIndex;
    setStudentAnswers(newAnswers);
  };

  const handleSubmit = () => {
    setIsExamOver(true);
    const exam = examConfig.examCodes.find(
      (e) => e.code === studentData.examCode
    );

    let correctCount = 0;
    exam.questionsData.forEach((q, index) => {
      if (studentAnswers[index] === q.correctAnswer) {
        correctCount++;
      }
    });

    const total = exam.questionsData.length;
    const passed = correctCount >= Math.ceil(total / 2);

    setScore(correctCount);
    setIsPassed(passed);
  };

  const handleRetry = () => {
    setTimeLeft(30 * 60); // Reset lại thời gian
    setIsExamOver(false); // Reset trạng thái thi
    setStudentAnswers([]); // Xóa câu trả lời
    setScore(null); // Xóa điểm
    setIsPassed(null); // Xóa trạng thái đã đạt hay chưa

    if (onRetry) onRetry();
  };

  const handleCreateNewRoom = () => {
    setStudentData({
      studentName: "",
      studentId: "",
      examCode: "",
      roomName: "",
    });

    if (onCreateNewRoom) onCreateNewRoom();
  };

  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    return `${minutes}:${seconds < 10 ? `0${seconds}` : seconds}`;
  };

  return (
    <div className="quiz-container">
      <h2>
        Đề {studentData.examCode} - Phòng {studentData.roomName}
      </h2>
      <p>
        Họ tên: {studentData.studentName} - MSSV: {studentData.studentId}
      </p>
      <p>Số câu hỏi: {examConfig.examCodes[0].questionsData.length}</p>

      <div className="timer" style={{ display: "flex", alignItems: "center", gap: "12px", position: "relative" }}>
        <h4>Thời gian còn lại: {formatTime(timeLeft)}</h4>
        <button
          className="menu-button"
          onClick={() => setIsMenuOpen(!isMenuOpen)}
          title="Danh sách câu hỏi"
        >
          ĐÂJODAOJDAOJ
        </button>

        {isMenuOpen && (
          <div className="question-menu">
            <h4>Danh sách câu hỏi</h4>
            <ul>
              {examConfig.examCodes[0].questionsData.map((q, index) => (
                <li key={index}>
                  <strong>Câu {index + 1}:</strong> {q.questionText.slice(0, 30)}...
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>

      {examConfig.examCodes[0].questionsData.map((q, index) => (
        <div key={index} className="question-box1">
          <h4>
            Câu {index + 1}: {q.questionText}
          </h4>
          <div className="answer-grid">
            {q.options.map((opt, i) => (
              <label key={i} className="answer-option">
                <input
                  type="radio"
                  name={`q${index}`}
                  value={i}
                  checked={studentAnswers[index] === i}
                  onChange={() => handleAnswerChange(index, i)}
                  disabled={isExamOver}
                />
                <span>
                  {String.fromCharCode(65 + i)}. {opt}
                </span>
              </label>
            ))}
          </div>
        </div>
      ))}

      {!isExamOver && (
        <button
          className="submit-button bg-blue-500 text-white p-2 mt-4 rounded"
          onClick={handleSubmit}
        >
          Nộp bài
        </button>
      )}

      {isExamOver && score !== null && (
        <div className="result mt-4" style={{ marginTop: "20px" }}>
          <h3>Thông tin thí sinh:</h3>
          <p><strong>Họ tên:</strong> {studentData.studentName}</p>
          <p><strong>MSSV:</strong> {studentData.studentId}</p>
          <p><strong>Mã đề:</strong> {studentData.examCode}</p>
          <p><strong>Phòng thi:</strong> {studentData.roomName}</p>
          <h3>
            Kết quả:{" "}
            <span
              style={{
                color: isPassed ? "green" : "red",
                fontWeight: "bold",
                fontSize: "18px",
              }}
            >
              {isPassed ? "ĐẠT" : "KHÔNG ĐẠT"}
            </span>{" "}
            ({score}/{examConfig.examCodes[0].questionsData.length} câu đúng)
          </h3>
          <button
            onClick={handleRetry}
            className="retry-button bg-yellow-500 text-white p-2 mt-2 rounded"
          >
            Thi lại
          </button>
        </div>
      )}
    </div>
  );
}

export default QuizApp;
