// App.js
import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, useNavigate } from "react-router-dom";
import QuizApp from "./QuizApp";
import CreateExamForm from "./CreateExamForm";
import CreateForm from "./CreateForm";
import "./App.css";

function AppWrapper() {
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [loginError, setLoginError] = useState("");
  const [showCreateExam, setShowCreateExam] = useState(false);
  const [numberOfQuestions, setNumberOfQuestions] = useState(1);
  const [startQuiz, setStartQuiz] = useState(false);

  const navigate = useNavigate();

  const toggleLogin = () => {
    setShowLogin(!showLogin);
    setShowRegister(false);
  };

  const toggleRegister = () => {
    setShowRegister(!showRegister);
    setShowLogin(false);
  };

  const handleRegister = (e) => {
    e.preventDefault();
    const username = e.target.username.value;
    const password = e.target.password.value;
    const email = e.target.email.value;
    const role = e.target.role.value;
    localStorage.setItem(
      "user",
      JSON.stringify({ username, password, email, role })
    );
    setShowRegister(false);
    alert("Đăng ký thành công!");
  };

  const handleLogin = (e) => {
    e.preventDefault();
    const username = e.target.username.value;
    const password = e.target.password.value;
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (
      storedUser &&
      storedUser.username === username &&
      storedUser.password === password
    ) {
      setCurrentUser(storedUser);
      setLoginError("");
      setShowLogin(false);
    } else {
      setLoginError("Tên đăng nhập hoặc mật khẩu không đúng.");
    }
  };

  const handleLogout = () => {
    setCurrentUser(null);
    setStartQuiz(false);
    setShowCreateExam(false);
  };

  const closeModal = () => {
    setShowLogin(false);
    setShowRegister(false);
  };

  const handleGoHome = () => {
    navigate("/");
  };

  const handleCreateQuiz = () => {
    if (!currentUser) {
      setShowLogin(true);
    } else if (currentUser.role !== "admin") {
      alert("Bạn phải là quản trị viên để tạo đề thi.");
    } else {
      navigate("/"); // về trang tạo đề thi
      setShowCreateExam(true);
      setStartQuiz(false);
    }
  };

  return (
    <div>
      <div className="navbar">
        <div className="navbar-left">
          <h2>QuizApp</h2>
        </div>
        <div className="navbar-right">
          <button className="nav-button" onClick={handleGoHome}>
            Trang chủ
          </button>
          {!currentUser ? (
            <>
              <button className="nav-button" onClick={toggleLogin}>
                Đăng nhập
              </button>
              <button className="nav-button" onClick={toggleRegister}>
                Đăng ký
              </button>
            </>
          ) : (
            <>
              <button className="nav-button" onClick={handleLogout}>
                Đăng xuất
              </button>
            </>
          )}
          <button className="nav-button" onClick={handleCreateQuiz}>
            Tạo đề thi
          </button>
          <button className="nav-button">Xem Kết Quả</button>
        </div>
      </div>

      {showLogin && (
        <div className="modal">
          <div className="form-box">
            <button className="close-btn" onClick={closeModal}>
              &times;
            </button>
            <h3>Đăng nhập</h3>
            <form onSubmit={handleLogin}>
              <input
                type="text"
                name="username"
                placeholder="Tên đăng nhập"
                required
              />
              <input
                type="password"
                name="password"
                placeholder="Mật khẩu"
                required
              />
              {loginError && <p style={{ color: "red" }}>{loginError}</p>}
              <button className="submit-button" type="submit">
                Đăng nhập
              </button>
            </form>
          </div>
        </div>
      )}

      {showRegister && (
        <div className="modal">
          <div className="form-box">
            <button className="close-btn" onClick={closeModal}>
              &times;
            </button>
            <h3>Đăng ký</h3>
            <form onSubmit={handleRegister}>
              <input
                type="text"
                name="username"
                placeholder="Tên người dùng"
                required
              />
              <input type="email" name="email" placeholder="Email" required />
              <input
                type="password"
                name="password"
                placeholder="Mật khẩu"
                required
              />
              <select name="role" required>
                <option value="user">Người dùng</option>
                <option value="admin">Quản trị viên</option>
              </select>
              <button className="submit-button" type="submit">
                Đăng ký
              </button>
            </form>
          </div>
        </div>
      )}

      {showCreateExam && currentUser?.role === "admin" && <CreateExamForm />}

      {startQuiz && numberOfQuestions && (
        <div className="quiz-container">
          <h1 className="quiz-title">Thi Giữa Kỳ - năm học 2025-2026</h1>
          <h2 className="quiz-subtitle">
            Môn: Xây Dựng Phần Mềm Web - Thời gian: 30 phút
          </h2>
          <p className="quiz-warning">Lưu ý: Không sử dụng tài liệu</p>
          <QuizApp numQuestions={numberOfQuestions} />
        </div>
      )}
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<AppWrapper />} />
        <Route path="/create-questions" element={<CreateForm />} />
      </Routes>
    </Router>
  );
}

export default App;
