import React, { useState } from "react";
import "./App.css";
import QuizForm from "./components/QuizForm";
import QuestionCard from "./QuestionCard";
function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [startQuiz, setStartQuiz] = useState(false);
  const [numberOfQuestions] = useState(5);

  const toggleLogin = () => {
    setShowLogin(!showLogin);
    setShowRegister(false);
  };

  const toggleRegister = () => {
    setShowRegister(!showRegister);
    setShowLogin(false);
  };

  const handleRegister = (e) => {
    e.preventDefault();
    const username = e.target.username.value;
    const password = e.target.password.value;
    const email = e.target.email.value;
    const role = e.target.role.value;

    localStorage.setItem(
      "user",
      JSON.stringify({ username, password, email, role })
    );
    alert("Đăng ký thành công!");
    setShowRegister(false);
  };

  const handleLogin = (e) => {
    e.preventDefault();
    const username = e.target.username.value;
    const password = e.target.password.value;
    const storedUser = JSON.parse(localStorage.getItem("user"));

    if (
      storedUser &&
      storedUser.username === username &&
      storedUser.password === password
    ) {
      setCurrentUser(storedUser);
      setShowLogin(false);
    } else {
      alert("Sai thông tin đăng nhập!");
    }
  };

  const handleLogout = () => {
    setCurrentUser(null);
    setStartQuiz(false);
  };

  const handleCreateQuiz = () => {
    if (!currentUser) {
      setShowLogin(true);
    } else if (currentUser.role !== "admin") {
      alert("Chỉ quản trị viên mới được tạo đề thi.");
    } else {
      setStartQuiz(true);
    }
  };

  return (
    <div>
      <div className="navbar">
        <h2>QuizApp</h2>
        <div>
          {!currentUser ? (
            <>
              <button onClick={toggleLogin}>Đăng nhập</button>
              <button onClick={toggleRegister}>Đăng ký</button>
            </>
          ) : (
            <button onClick={handleLogout}>Đăng xuất</button>
          )}
          <button onClick={handleCreateQuiz}>Tạo đề thi</button>
        </div>
      </div>

      {showLogin && (
        <div className="modal">
          <form onSubmit={handleLogin}>
            <input name="username" placeholder="Tên đăng nhập" />
            <input type="password" name="password" placeholder="Mật khẩu" />
            <button type="submit">Đăng nhập</button>
          </form>
        </div>
      )}

      {showRegister && (
        <div className="modal">
          <form onSubmit={handleRegister}>
            <input name="username" placeholder="Tên người dùng" />
            <input name="email" placeholder="Email" />
            <input name="password" placeholder="Mật khẩu" />
            <select name="role">
              <option value="user">Người dùng</option>
              <option value="admin">Quản trị viên</option>
            </select>
            <button type="submit">Đăng ký</button>
          </form>
        </div>
      )}

      {startQuiz && <QuizForm numberOfQuestions={numberOfQuestions} />}
    </div>
  );
}

export default App;
