import React, { useEffect, useState } from "react";

function QuizApp({ onRetry, onCreateNewRoom }) {
  const [studentData, setStudentData] = useState({
    studentName: "Nguyễn Văn A",
    studentId: "123456789",
    examCode: "EX123",
    roomName: "Phòng 1",
  });

  const [examConfig, setExamConfig] = useState({
    examCodes: [
      {
        code: "EX123",
        questionsData: [
          {
            questionText: "Câu hỏi 1: question la cai cho gi = ?",
            options: [
              "mot cai gi do rat xam lz",
              "dung hoi nưa khong muon biet",
              "len chatgpt di choi ",
              "hahahahahahaha"
            ],
            correctAnswer: 1,
          },
          {
            questionText: "Câu hỏi 2: 3 + 5 = ?",
            options: ["7", "8", "9", "10"],
            correctAnswer: 1,
          },
          {
            questionText: "Câu hỏi 3: 5 + 7 = ?",
            options: ["11", "12", "13", "14"],
            correctAnswer: 1,
          },
        ],
      },
    ],
  });

  const [timeLeft, setTimeLeft] = useState(30 * 60); // 30 phút
  const [isExamOver, setIsExamOver] = useState(false);
  const [studentAnswers, setStudentAnswers] = useState([]);
  const [score, setScore] = useState(null);
  const [isPassed, setIsPassed] = useState(null);

  // Tạo một effect để xử lý đồng hồ đếm ngược
  useEffect(() => {
    if (isExamOver) return; // Không chạy timer nếu đã hết giờ
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => {
        if (prevTime <= 0) {
          clearInterval(timer);
          handleSubmit();
          return 0;
        }
        return prevTime - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [isExamOver]);

  const handleAnswerChange = (questionIndex, answerIndex) => {
    const newAnswers = [...studentAnswers];
    newAnswers[questionIndex] = answerIndex;
    setStudentAnswers(newAnswers);
  };

  const handleSubmit = () => {
    setIsExamOver(true);
    const exam = examConfig.examCodes.find(
      (e) => e.code === studentData.examCode
    );

    let correctCount = 0;
    exam.questionsData.forEach((q, index) => {
      if (studentAnswers[index] === q.correctAnswer) {
        correctCount++;
      }
    });

    const total = exam.questionsData.length;
    const passed = correctCount >= Math.ceil(total / 2);

    setScore(correctCount);
    setIsPassed(passed);
  };
  const CreateExamRoom = ({ examConfig, goToQuizApp, currentUser }) =>{
     const [inputCode, setInputCode] = useState("");
      const [roomName, setRoomName] = useState("");
      const [isCodeValid, setIsCodeValid] = useState(false);
      const [selectedExamInfo, setSelectedExamInfo] = useState(null);
      const [studentName, setStudentName] = useState("");
      const [studentId, setStudentId] = useState("");
    
      // ✅ Reset trạng thái mỗi khi component render lại
      useEffect(() => {
        setInputCode("");
        setRoomName("");
        setIsCodeValid(false);
        setSelectedExamInfo(null);
        setStudentName("");
        setStudentId("");
      }, []); // Chạy một lần khi component mount
    
      if (currentUser?.role === "admin") {
        return (
          <div className="center-screen">
            <div className="form-box">
              <h2>Không thể vào phòng thi</h2>
              <p style={{ color: "red" }}>
                Tài khoản quản trị viên không được phép tham gia thi.
              </p>
            </div>
          </div>
        );
      }
    
      const handleCheckCode = (e) => {
        e.preventDefault();
        const examIndex = parseInt(inputCode) - 1;
    
        if (
          examConfig?.examCodes &&
          examIndex >= 0 &&
          examIndex < examConfig.examCodes.length
        ) {
          const selected = examConfig.examCodes[examIndex];
          setSelectedExamInfo(selected);
          setIsCodeValid(true);
        } else {
          alert("❌ Mã đề không hợp lệ.");
        }
      };
    
      const handleStartExam = (e) => {
        e.preventDefault();
        const studentData = {
          roomName,
          examCode: inputCode,
          studentName,
          studentId,
        };
    
        alert("✅ Vào thi thành công!");
        goToQuizApp(studentData);
      };
    
      return (
        <div className="center-screen">
          <div className="form-box">
            <h3>Vào phòng thi</h3>
    
            {!isCodeValid ? (
              <form onSubmit={handleCheckCode}>
                <label>Nhập mã đề:</label>
                <input
                  value={inputCode}
                  onChange={(e) => setInputCode(e.target.value)}
                  required
                  placeholder="VD: 1"
                />
                <label>Tên phòng thi:</label>
                <input
                  value={roomName}
                  onChange={(e) => setRoomName(e.target.value)}
                  required
                />
                <button className="submit-button">Kiểm tra mã đề</button>
              </form>
            ) : (
              <form onSubmit={handleStartExam}>
                <p style={{ color: "green" }}>✅ Mã đề hợp lệ</p>
    
                <div className="exam-info-box">
                  <p>
                    <strong>Số câu hỏi:</strong> {selectedExamInfo?.questionsData?.length || 0}
                  </p>
                  <p>
                    <strong>Thời gian làm bài 30 phút</strong>
                  </p>
                </div>
    
                <label>Họ tên sinh viên:</label>
                <input
                  value={studentName}
                  onChange={(e) => setStudentName(e.target.value)}
                  required
                />
                <label>MSSV:</label>
                <input
                  value={studentId}
                  onChange={(e) => setStudentId(e.target.value)}
                  required
                />
                <button className="submit-button">Bắt đầu thi</button>
              </form>
            )}
          </div>
        </div>
      );

  }
  const handleRetry = () => {
    // Reset tất cả trạng thái để quay lại thi
    setTimeLeft(30 * 60); // Reset lại thời gian
    setIsExamOver(false); // Reset trạng thái thi
    setStudentAnswers([]); // Xóa câu trả lời
    setScore(null); // Xóa điểm
    setIsPassed(null); // Xóa trạng thái đã đạt hay chưa

    // Gọi lại onRetry nếu có, để điều khiển việc chuyển trang hoặc trở về trang tạo phòng thi
    if (onRetry) onRetry();
  };

  
  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    return `${minutes}:${seconds < 10 ? `0${seconds}` : seconds}`;
  };

  return (
    <div className="quiz-container">
      <h2>
        Đề {studentData.examCode} - Phòng {studentData.roomName}
      </h2>
      <p>
        Họ tên: {studentData.studentName} - MSSV: {studentData.studentId}
      </p>
      <p>Số câu hỏi: {examConfig.examCodes[0].questionsData.length}</p>

      <div className="timer">
        <h4>Thời gian còn lại: {formatTime(timeLeft)}</h4>
        {isExamOver && <p>Thời gian đã hết!</p>}
      </div>

      {examConfig.examCodes[0].questionsData.map((q, index) => (
        <div key={index} className="question-box1">
          <h4>
            Câu {index + 1}: {q.questionText}
          </h4>
          <div className="answer-grid">
            {q.options.map((opt, i) => (
              <label key={i} className="answer-option">
                <input
                  type="radio"
                  name={`q${index}`}
                  value={i}
                  checked={studentAnswers[index] === i}
                  onChange={() => handleAnswerChange(index, i)}
                  disabled={isExamOver}
                />
                <span>
                  {String.fromCharCode(65 + i)}. {opt}
                </span>
              </label>
            ))}
          </div>
        </div>
      ))}

      {!isExamOver && (
        <button
          className="submit-button bg-blue-500 text-white p-2 mt-4 rounded"
          onClick={handleSubmit}
        >
          Nộp bài
        </button>
      )}

      {isExamOver && score !== null && (
        <div className="result mt-4" style={{ marginTop: "20px" }}>
          <h3>Thông tin thí sinh:</h3>
          <p><strong>Họ tên:</strong> {studentData.studentName}</p>
          <p><strong>MSSV:</strong> {studentData.studentId}</p>
          <p><strong>Mã đề:</strong> {studentData.examCode}</p>
          <p><strong>Phòng thi:</strong> {studentData.roomName}</p>
          <h3>
            Kết quả:{" "}
            <span
              style={{
                color: isPassed ? "green" : "red",
                fontWeight: "bold",
                fontSize: "18px",
              }}
            >
              {isPassed ? "ĐẠT" : "KHÔNG ĐẠT"}
            </span>{" "}
            ({score}/{examConfig.examCodes[0].questionsData.length} câu đúng)
          </h3>
          <button
            onClick={hhandleCheckCode}
            className="retry-button bg-yellow-500 text-white p-2 mt-2 rounded"
          >
            Thi lại
          </button>
          
        </div>
      )}
    </div>
  );
}

export default QuizApp;
