import React, { useState } from "react";

function QuizApp({ numQuestions }) {
  const [answers, setAnswers] = useState(new Array(numQuestions).fill(null)); // Stores answers
  const [submitted, setSubmitted] = useState(false); // Whether the quiz is submitted or not
  const [score, setScore] = useState(0); // To store the score (number of correct answers)

  const correctAnswers = ["A", "B", "C", "D", "A", "B", "C", "D", "A", "B"]; // Example correct answers

  const handleAnswerChange = (index, value) => {
    const updatedAnswers = [...answers];
    updatedAnswers[index] = value;
    setAnswers(updatedAnswers);
  };

  const handleSubmit = () => {
    let correctCount = 0;

    // Check the user's answers against the correct answers
    for (let i = 0; i < numQuestions; i++) {
      if (answers[i] === correctAnswers[i]) {
        correctCount++;
      }
    }

    setScore(correctCount);
    setSubmitted(true);
  };

  const passThreshold = Math.floor(numQuestions / 2); // Example: Passing is 50% correct

  return (
    <div>
      <h3>Quiz - {numQuestions} Questions</h3>

      <form>
        {Array.from({ length: numQuestions }).map((_, index) => (
          <div key={index}>
            <h4>Question {index + 1}</h4>
            <label>
              <input
                type="radio"
                name={`question${index}`}
                value="A"
                checked={answers[index] === "A"}
                onChange={() => handleAnswerChange(index, "A")}
              />
              A
            </label>
            <label>
              <input
                type="radio"
                name={`question${index}`}
                value="B"
                checked={answers[index] === "B"}
                onChange={() => handleAnswerChange(index, "B")}
              />
              B
            </label>
            <label>
              <input
                type="radio"
                name={`question${index}`}
                value="C"
                checked={answers[index] === "C"}
                onChange={() => handleAnswerChange(index, "C")}
              />
              C
            </label>
            <label>
              <input
                type="radio"
                name={`question${index}`}
                value="D"
                checked={answers[index] === "D"}
                onChange={() => handleAnswerChange(index, "D")}
              />
              D
            </label>
          </div>
        ))}
      </form>

      {!submitted ? (
        <button onClick={handleSubmit}>Nộp bài</button>
      ) : (
        <div>
          <h2>
            {score >= passThreshold
              ? "Đạt"
              : "Không Đạt"} - {score} / {numQuestions}
          </h2>
        </div>
      )}
    </div>
  );
}

export default QuizApp;
