import React, { useState } from "react";
import QuizApp from "./QuizApp";
import "./App.css";


function CreateExamForm() {
  const [examName, setExamName] = useState("");
  const [grade, setGrade] = useState("");
  const [subject, setSubject] = useState("");
  const [scoring, setScoring] = useState({
    correct1: "",
    correct2: "",
    correct3: "",
    correct4: "",
  });

  const [examCodes, setExamCodes] = useState([
    {
      id: 1,
      questions: 5,
      totalScore: 8,
      answers: Array(5).fill(""),
    },
  ]);

  const handleAnswerChange = (codeIndex, questionIndex, value) => {
    const updated = [...examCodes];
    updated[codeIndex].answers[questionIndex] = value;
    setExamCodes(updated);
  };

  const handleQuestionCountChange = (index, value) => {
    const updated = [...examCodes];
    const num = parseInt(value);
    updated[index].questions = num;
    updated[index].answers = Array(num).fill("");
    setExamCodes(updated);
  };

  const handleTotalScoreChange = (index, value) => {
    const updated = [...examCodes];
    updated[index].totalScore = parseFloat(value);
    setExamCodes(updated);
  };

  const addExamCode = () => {
    setExamCodes([
      ...examCodes,
      {
        id: examCodes.length + 1,
        questions: 5,
        totalScore: 10,
        answers: Array(5).fill(""),
      },
    ]);
  };

  const calculateScorePerQuestion = (code) => {
    return (code.totalScore / code.questions).toFixed(2);
  };

  const handleSave = () => {
    alert("Đề thi đã được lưu!");
    // Xử lý lưu vào backend nếu cần
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h2>Tạo đề thi</h2>

      <div>
        <label>Tên đề:</label>
        <input
          value={examName}
          onChange={(e) => setExamName(e.target.value)}
          placeholder="Nhập tên đề..."
        />
      </div>

      <div style={{ display: "flex", gap: "20px", marginTop: "10px" }}>
        <div>
          <label>Khối học:</label>
          <select value={grade} onChange={(e) => setGrade(e.target.value)}>
            <option value="">---Chọn khối---</option>
            <option value="10">Khối 10</option>
            <option value="11">Khối 11</option>
            <option value="12">Khối 12</option>
          </select>
        </div>
        <div>
          <label>Môn học:</label>
          <select value={subject} onChange={(e) => setSubject(e.target.value)}>
            <option value="">---Chọn môn---</option>
            <option value="toan">Toán</option>
            <option value="van">Văn</option>
            <option value="ly">Lý</option>
          </select>
        </div>
      </div>

      <h4 style={{ marginTop: "20px" }}>Cấu hình thang điểm:</h4>
      <div style={{ display: "grid", gridTemplateColumns: "repeat(2, 1fr)", gap: "10px" }}>
        {["correct1", "correct2", "correct3", "correct4"].map((key, idx) => (
          <div key={key}>
            <label>
              Trả lời đúng {idx + 1} ý:
              <input
                type="number"
                value={scoring[key]}
                onChange={(e) => setScoring({ ...scoring, [key]: e.target.value })}
                placeholder="%"
              />
            </label>
          </div>
        ))}
      </div>

      <h4 style={{ marginTop: "20px" }}>Danh sách mã đề</h4>
      {examCodes.map((code, index) => (
        <div
          key={index}
          style={{
            border: "1px solid #ccc",
            padding: "15px",
            marginBottom: "10px",
            borderRadius: "10px",
          }}
        >
          <div style={{ marginBottom: "10px" }}>
            <strong>Phần trắc nghiệm:</strong>
            <label style={{ marginLeft: "10px" }}>
              Số lượng câu hỏi:
              <input
                type="number"
                min="1"
                value={code.questions}
                onChange={(e) => handleQuestionCountChange(index, e.target.value)}
              />
            </label>
            <label style={{ marginLeft: "10px" }}>
              Tổng điểm:
              <input
                type="number"
                min="0"
                value={code.totalScore}
                onChange={(e) => handleTotalScoreChange(index, e.target.value)}
              />
            </label>
            <span style={{ marginLeft: "10px" }}>
              Số điểm mỗi câu: {calculateScorePerQuestion(code)}
            </span>
          </div>

          
        </div>
      ))}

      <button onClick={addExamCode} style={{ background: "green", color: "white", padding: "5px 10px", marginBottom: "10px" }}>
        + Thêm mã đề
      </button>

      <div style={{ display: "flex", gap: "10px" }}>
        <button onClick={handleSave} style={{ background: "black", color: "white", padding: "10px 20px" }}>
          Lưu
        </button>
        <button style={{ background: "#ccc", padding: "10px 20px" }}>Hủy</button>
      </div>
    </div>
  );
}

export default CreateExamForm;
