import React, { useState } from "react";
import QuizApp from "./QuizApp";
import CreateExamForm from "./CreateExamForm";
import CreateForm from "./CreateForm";
import CreateExamRoom from "./CreateExamRoom";
import "./App.css";

function App() {
  const [currentPage, setCurrentPage] = useState("home");
  const [currentUser, setCurrentUser] = useState(null);
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [loginError, setLoginError] = useState("");
  const [examConfig, setExamConfig] = useState(null);
  const [quizStarted, setQuizStarted] = useState(false);

  // Chuy·ªÉn ƒë·ªïi modal login/register
  const toggleLogin = () => {
    setShowLogin(!showLogin);
    setShowRegister(false);
  };

  const toggleRegister = () => {
    setShowRegister(!showRegister);
    setShowLogin(false);
  };

  // X·ª≠ l√Ω ƒëƒÉng nh·∫≠p
  const handleLogin = (e) => {
    e.preventDefault();
    const username = e.target.username.value;
    const password = e.target.password.value;
    const storedUser = JSON.parse(localStorage.getItem("user"));

    if (
      storedUser &&
      storedUser.username === username &&
      storedUser.password === password
    ) {
      setCurrentUser(storedUser);
      setLoginError("");
      setShowLogin(false);
    } else {
      setLoginError("‚ùå T√™n ƒëƒÉng nh·∫≠p ho·∫∑c m·∫≠t kh·∫©u kh√¥ng ƒë√∫ng.");
    }
  };

  // X·ª≠ l√Ω ƒëƒÉng k√Ω
  const handleRegister = (e) => {
    e.preventDefault();
    const user = {
      username: e.target.username.value,
      password: e.target.password.value,
      email: e.target.email.value,
      role: e.target.role.value,
    };
    localStorage.setItem("user", JSON.stringify(user));
    alert("‚úÖ ƒêƒÉng k√Ω th√†nh c√¥ng!");
    setShowRegister(false);
  };

  // ƒêƒÉng xu·∫•t
  const handleLogout = () => {
    setCurrentUser(null);
    setCurrentPage("home");
  };

  // Ki·ªÉm tra quy·ªÅn tr∆∞·ªõc khi t·∫°o ƒë·ªÅ/ph√≤ng
  const handleCreateQuiz = () => {
    if (!currentUser) {
      setShowLogin(true);
    } else if (currentUser.role !== "admin") {
      alert("‚ö†Ô∏è B·∫°n ph·∫£i l√† qu·∫£n tr·ªã vi√™n ƒë·ªÉ t·∫°o ƒë·ªÅ thi.");
    } else {
      setCurrentPage("createExam");
    }
  };

  const handleCreateRoom = () => {
    if (!currentUser) {
      setShowLogin(true);
    } else if (currentUser.role !== "admin") {
      alert("‚ö†Ô∏è B·∫°n ph·∫£i l√† qu·∫£n tr·ªã vi√™n ƒë·ªÉ t·∫°o ph√≤ng thi.");
    } else {
      setCurrentPage("createRoom");
    }
  };

  return (
    <div>
      {/* Navbar */}
      <div className="navbar">
        <div className="navbar-left">
          <h2>QuizApp</h2>
        </div>
        <div className="navbar-right">
          <button className="nav-button" onClick={() => setCurrentPage("home")}>
            Trang ch·ªß
          </button>
          <button className="nav-button" onClick={() => setCurrentPage("result")}>
            Xem k·∫øt qu·∫£
          </button>
          {!currentUser ? (
            <>
              <button className="nav-button" onClick={toggleLogin}>
                ƒêƒÉng nh·∫≠p
              </button>
              <button className="nav-button" onClick={toggleRegister}>
                ƒêƒÉng k√Ω
              </button>
            </>
          ) : (
            <>
              <span style={{ marginRight: 10 }}>
                üë§ {currentUser.username} ({currentUser.role})
              </span>
              <button className="nav-button" onClick={handleLogout}>
                ƒêƒÉng xu·∫•t
              </button>
              <button className="nav-button" onClick={handleCreateQuiz}>
                T·∫°o ƒë·ªÅ thi
              </button>
              <button className="nav-button" onClick={handleCreateRoom}>
                T·∫°o ph√≤ng thi
              </button>
            </>
          )}
        </div>
      </div>

      {/* Modal ƒêƒÉng nh·∫≠p */}
      {showLogin && (
        <div className="modal">
          <div className="form-box">
            <button className="close-btn" onClick={() => setShowLogin(false)}>
              &times;
            </button>
            <h3>ƒêƒÉng nh·∫≠p</h3>
            <form onSubmit={handleLogin}>
              <input type="text" name="username" placeholder="T√™n ƒëƒÉng nh·∫≠p" required />
              <input type="password" name="password" placeholder="M·∫≠t kh·∫©u" required />
              {loginError && <p style={{ color: "red" }}>{loginError}</p>}
              <button type="submit" className="submit-button">
                ƒêƒÉng nh·∫≠p
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Modal ƒêƒÉng k√Ω */}
      {showRegister && (
        <div className="modal">
          <div className="form-box">
            <button className="close-btn" onClick={() => setShowRegister(false)}>
              &times;
            </button>
            <h3>ƒêƒÉng k√Ω</h3>
            <form onSubmit={handleRegister}>
              <input name="username" placeholder="T√™n ng∆∞·ªùi d√πng" required />
              <input name="email" type="email" placeholder="Email" required />
              <input name="password" type="password" placeholder="M·∫≠t kh·∫©u" required />
              <select name="role" required>
                <option value="user">Ng∆∞·ªùi d√πng</option>
                <option value="admin">Qu·∫£n tr·ªã vi√™n</option>
              </select>
              <button className="submit-button">ƒêƒÉng k√Ω</button>
            </form>
          </div>
        </div>
      )}


      {/* N·ªôi dung theo trang hi·ªán t·∫°i */}
      {currentPage === "home" && (
        <div style={{ padding: 20 }}>
          <h3>Ch√†o m·ª´ng ƒë·∫øn v·ªõi QuizApp!</h3>
          <p>Vui l√≤ng ƒëƒÉng nh·∫≠p ho·∫∑c ƒëƒÉng k√Ω ƒë·ªÉ b·∫Øt ƒë·∫ßu.</p>
        </div>
      )}

      {currentPage === "createExam" && (
        <CreateExamForm
          setExamConfig={setExamConfig}
          goToCreateForm={() => setCurrentPage("createForm")}
        />
      )}

      {currentPage === "createForm" && examConfig && (
        <CreateForm examConfig={examConfig} />
      )}

      {currentPage === "createRoom" && (
        <CreateExamRoom
          examConfig={examConfig}
          goToQuizApp={() => setCurrentPage("quiz")}
        />
      )}

{screen === "quiz" && examConfig && studentInfo && (
  <QuizApp examConfig={examConfig} studentInfo={studentInfo} />
)}

      {currentPage === "result" && (
        <div style={{ padding: 20 }}>
          <h3>K·∫øt qu·∫£ l√†m b√†i (placeholder)</h3>
          {/* TODO: Th√™m danh s√°ch k·∫øt qu·∫£ sau */}
        </div>
      )}

    </div>

  );
}

export default App;
