import React, { useEffect, useState } from "react";

function QuizApp() {
  const [studentData, setStudentData] = useState(null);
  const [examConfig, setExamConfig] = useState(null);

  useEffect(() => {
    const storedStudentData = JSON.parse(localStorage.getItem("studentData"));
    const storedExamConfig = JSON.parse(localStorage.getItem("examConfig"));
    setStudentData(storedStudentData);
    setExamConfig(storedExamConfig);
  }, []);

  if (!studentData || !examConfig) return <p>Đang tải dữ liệu...</p>;

  const examCode = studentData.examCode;
  const exam = examConfig.examCodes.find(code => code.code === examCode);

  return (
    <div>
      <h2>Đề {examCode} - Phòng: {studentData.roomName}</h2>
      <p>Họ tên: {studentData.studentName} - MSSV: {studentData.studentId}</p>
      <p>Số câu: {exam.questions} - Tổng điểm: {exam.totalScore}</p>
      {/* TODO: render các câu hỏi tại đây */}
    </div>
  );
}

export default QuizApp;
