// CreateExamRoom.js
import React, { useState } from "react";

function CreateExamRoom({ examConfig }) {
  const [roomName, setRoomName] = useState("");
  const [candidateName, setCandidateName] = useState("");
  const [candidateEmail, setCandidateEmail] = useState("");

  const handleCreateRoom = (e) => {
    e.preventDefault();
    const roomData = {
      roomName,
      examCode: examConfig?.examCode || "CHUA_CO_MA",
      candidateName,
      candidateEmail,
    };
    console.log("Tạo phòng với thông tin:", roomData);
    alert("Tạo phòng thành công!");
    // Có thể lưu vào localStorage hoặc gửi lên backend sau
  };

  return (
    <div className="center-screen">
      <div className="form-box">
        <h3>Tạo phòng thi</h3>
        <form onSubmit={handleCreateRoom}>
          <label>Mã đề thi:</label>
          <input value={examConfig?.examCode || ""} readOnly />
  
          <label>phòng thi:</label>
          <input
            type="text"
            value={roomName}
            onChange={(e) => setRoomName(e.target.value)}
            required
          />
  
          <button type="submit" className="submit-button">Tạo phòng</button>
        </form>
      </div>
    </div>
  );
}

export default CreateExamRoom;
