import React, { useState } from "react";

function CreateExamRoom({ examConfig }) {
  const [inputCode, setInputCode] = useState("");
  const [isCodeValid, setIsCodeValid] = useState(false);

  const [studentName, setStudentName] = useState("");
  const [studentId, setStudentId] = useState("");

  const handleCheckCode = (e) => {
    e.preventDefault();
    if (inputCode === examConfig?.examCode) {
      setIsCodeValid(true);
    } else {
      alert("Mã đề không đúng. Vui lòng kiểm tra lại.");
    }
  };

  const handleStartExam = (e) => {
    e.preventDefault();
    const studentData = {
      examCode: inputCode,
      studentName,
      studentId,
    };
    console.log("Sinh viên vào thi:", studentData);
    alert("Bắt đầu làm bài!");
    // Có thể chuyển sang trang QuizApp tại đây
  };

  return (
    <div className="center-screen">
      <div className="form-box">
        <h3>Vào phòng thi</h3>

        {!isCodeValid ? (
          <form onSubmit={handleCheckCode}>
            <label>Nhập mã đề thi:</label>
            <input
              type="text"
              value={inputCode}
              onChange={(e) => setInputCode(e.target.value)}
              required
            />
             <label>Nhập mã đề thi:</label>
            <input
              type="text"
              value={inputCode}
              onChange={(e) => setInputCode(e.target.value)}
              required
            />
            <button type="submit" className="submit-button">
              Kiểm tra mã đề
            </button>
          </form>
        ) : (
          <form onSubmit={handleStartExam}>
            <p style={{ color: "green" }}>✅ Mã đề hợp lệ: {inputCode}</p>
            <label>Họ và tên sinh viên:</label>
            <input
              type="text"
              value={studentName}
              onChange={(e) => setStudentName(e.target.value)}
              required
            />
            <label>Mã số sinh viên:</label>
            <input
              type="text"
              value={studentId}
              onChange={(e) => setStudentId(e.target.value)}
              required
            />
            <button type="submit" className="submit-button">
              Bắt đầu thi
            </button>
          </form>
        )}
      </div>
    </div>
  );
}

export default CreateExamRoom;
