import React, { useState, useEffect } from "react";
import QuizApp from "./QuizApp";
import CreateExamForm from "./CreateExamForm";
import CreateForm from "./CreateForm";
import CreateExamRoom from "./CreateExamRoom";
import "./App.css";

function App() {
  const [currentPage, setCurrentPage] = useState("home");
  const [currentUser, setCurrentUser] = useState(null);
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [loginError, setLoginError] = useState("");
  const [examConfig, setExamConfig] = useState(null);
  const [studentInfo, setStudentInfo] = useState(null);
  const [studentAnswers, setStudentAnswers] = useState([]);
  const [loginFormData, setLoginFormData] = useState({
    username: "",
    password: "",
    remember: false,
  });

  const toggleModal = (type) => {
    if (type === "login") {
      setShowLogin(true);
      setShowRegister(false);
    } else {
      setShowLogin(false);
      setShowRegister(true);
    }
  };

  const handleLoginChange = (e) => {
    const { name, value, type, checked } = e.target;
    setLoginFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleLogin = (e) => {
    e.preventDefault();
    const { username, password, remember } = loginFormData;
    const storedUser = JSON.parse(localStorage.getItem("user"));

    if (storedUser?.username === username && storedUser?.password === password) {
      setCurrentUser(storedUser);
      setLoginError("");
      setShowLogin(false);

      remember
        ? (localStorage.setItem("username", username), localStorage.setItem("password", password))
        : (localStorage.removeItem("username"), localStorage.removeItem("password"));
    } else {
      setLoginError("‚ùå T√™n ƒëƒÉng nh·∫≠p ho·∫∑c m·∫≠t kh·∫©u kh√¥ng ƒë√∫ng.");
    }
  };

  const handleRegister = (e) => {
    e.preventDefault();
    const newUser = {
      username: e.target.username.value,
      password: e.target.password.value,
      email: e.target.email.value,
      role: e.target.role.value || "user",
    };
    localStorage.setItem("user", JSON.stringify(newUser));
    alert("‚úÖ ƒêƒÉng k√Ω th√†nh c√¥ng!");
    setShowRegister(false);
  };

  const handleLogout = () => {
    setCurrentUser(null);
    setCurrentPage("home");
  };

  const handleCreateQuiz = () => {
    if (!currentUser) return setShowLogin(true);
    if (currentUser.role !== "admin") return alert("‚ö†Ô∏è B·∫°n ph·∫£i l√† gi√°o vi√™n ƒë·ªÉ t·∫°o ƒë·ªÅ thi.");
    setCurrentPage("createExam");
  };

  const handleCreateRoom = () => {
    if (!currentUser) return setShowLogin(true);
    if (!["admin", "user"].includes(currentUser.role)) {
      return alert("‚ö†Ô∏è B·∫°n ph·∫£i l√† gi√°o vi√™n m·ªõi t·∫°o ƒë∆∞·ª£c ph√≤ng thi.");
    }
    setCurrentPage("createRoom");
  };

  useEffect(() => {
    const defaultAdmin = localStorage.getItem("user");
    if (!defaultAdmin) {
      const admin = {
        username: "admin",
        password: "1",
        email: "admin@example.com",
        role: "admin",
      };
      localStorage.setItem("user", JSON.stringify(admin));
    }

