import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";

function CreateForm({ questionCount: propQuestionCount = 0, onBack }) {
  const location = useLocation?.();
  const navigate = useNavigate?.();

  // Ưu tiên lấy từ location.state, fallback về props
  const questionCount = location?.state?.questionCount || propQuestionCount;

  // Khởi tạo mảng câu hỏi
  const [questions, setQuestions] = useState([]);

  useEffect(() => {
    const initialQuestions = Array.from({ length: questionCount }, () => ({
      content: "",
      answers: { A: "", B: "", C: "", D: "" },
      correct: "A",
    }));
    setQuestions(initialQuestions);
  }, [questionCount]);

  const handleInputChange = (index, field, value) => {
    const updated = [...questions];
    updated[index][field] = value;
    setQuestions(updated);
  };

  const handleAnswerChange = (index, option, value) => {
    const updated = [...questions];
    updated[index].answers[option] = value;
    setQuestions(updated);
  };

  const handleCorrectChange = (index, value) => {
    const updated = [...questions];
    updated[index].correct = value;
    setQuestions(updated);
  };

  const handleSave = () => {
    console.log("Đề thi đã tạo:", questions);
    alert("Đề thi đã được lưu!");

    if (onBack) {
      onBack(); // dùng thủ công
    } else if (navigate) {
      navigate("/"); // dùng router
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Nhập nội dung đề thi ({questionCount} câu hỏi)</h2>

      {questions.map((q, idx) => (
        <div
          key={idx}
          style={{
            border: "1px solid #ccc",
            borderRadius: "5px",
            padding: "10px",
            marginBottom: "15px",
          }}
        >
          <p>
            <strong>Câu {idx + 1}:</strong>
          </p>
          <input
            type="text"
            placeholder="Nhập nội dung câu hỏi"
            value={q.content}
            onChange={(e) => handleInputChange(idx, "content", e.target.value)}
            style={{ width: "100%", padding: "5px", marginBottom: "10px" }}
          />

          <div style={{ display: "flex", flexWrap: "wrap", gap: "10px" }}>
            {["A", "B", "C", "D"].map((option) => (
              <div key={option} style={{ flex: "1 1 45%" }}>
                <label>
                  {option}:
                  <input
                    type="text"
                    value={q.answers[option]}
                    onChange={(e) =>
                      handleAnswerChange(idx, option, e.target.value)
                    }
                    style={{ width: "100%", padding: "5px", marginTop: "3px" }}
                  />
                </label>
              </div>
            ))}
          </div>

          <div style={{ marginTop: "10px" }}>
            <label>Đáp án đúng: </label>
            <select
              value={q.correct}
              onChange={(e) => handleCorrectChange(idx, e.target.value)}
              style={{ padding: "5px" }}
            >
              {["A", "B", "C", "D"].map((opt) => (
                <option key={opt} value={opt}>
                  {opt}
                </option>
              ))}
            </select>
          </div>
        </div>
      ))}

      <button onClick={handleSave} style={{ padding: "10px 20px" }}>
        Lưu đề thi
      </button>
    </div>
  );
}

export default CreateForm;
