import React, { useState } from "react";
import "./QuizApp.css"; // Đảm bảo file CSS này tồn tại cùng thư mục

function CreateExamForm({ setExamConfig, goToCreateForm }) {
  const [examName, setExamName] = useState("");
  const [grade, setGrade] = useState("");
  const [subject, setSubject] = useState("");
  const [scoring, setScoring] = useState({
    correct1: "",
    correct2: "",
    correct3: "",
    correct4: "",
  });

  const [examCodes, setExamCodes] = useState([
    {
      id: 1,
      questions: 5,
      totalScore: 8,
      questionsData: Array(5).fill({
        questionText: "",
        options: { A: "", B: "", C: "", D: "" },
        correctAnswer: "A",
      }),
    },
  ]);

  const handleQuestionCountChange = (index, value) => {
    const updated = [...examCodes];
    const num = parseInt(value);
    updated[index].questions = num;
    updated[index].questionsData = Array(num).fill({
      questionText: "",
      options: { A: "", B: "", C: "", D: "" },
      correctAnswer: "A",
    });
    setExamCodes(updated);
  };

  const handleTotalScoreChange = (index, value) => {
    const updated = [...examCodes];
    updated[index].totalScore = parseFloat(value);
    setExamCodes(updated);
  };

  const handleQuestionDataChange = (examCodeIndex, questionIndex, field, value) => {
    const updated = [...examCodes];
    updated[examCodeIndex].questionsData[questionIndex][field] = value;
    setExamCodes(updated);
  };

  const addExamCode = () => {
    const newId = examCodes.length + 1;
    const newCode = {
      id: newId,
      questions: 5,
      totalScore: 10,
      questionsData: Array(5).fill({
        questionText: "",
        options: { A: "", B: "", C: "", D: "" },
        correctAnswer: "A",
      }),
    };
    setExamCodes([...examCodes, newCode]);
  };

  const calculateScorePerQuestion = (code) => {
    return (code.totalScore / code.questions).toFixed(2);
  };

  const handleSave = () => {
    const examData = {
      examName,
      grade,
      subject,
      scoring,
      examCodes,
    };

    setExamConfig(examData); // Truyền dữ liệu về App.js
    goToCreateForm(); // Điều hướng sang CreateForm

    console.log("Đề thi đã được lưu:", examData);
  };

  const handleCancel = () => {
    window.location.href = "/";
  };

  return (
    <div className="create-exam-container">
      <h2 className="form-title">Tạo đề thi</h2>

      <div className="form-group">
        <label>Tên đề:</label>
        <input
          className="form-input"
          value={examName}
          onChange={(e) => setExamName(e.target.value)}
          placeholder="Nhập tên đề..."
        />
      </div>

      <div className="form-group-row">
        <div>
          <label>Khối học:</label>
          <input
            className="form-input"
            value={grade}
            onChange={(e) => setGrade(e.target.value)}
            placeholder="Nhập khối học..."
          />
        </div>
        <div>
          <label>Môn học:</label>
          <input
            className="form-input"
            value={subject}
            onChange={(e) => setSubject(e.target.value)}
            placeholder="Nhập môn học..."
          />
        </div>
      </div>

      <h4 className="form-subtitle">Danh sách mã đề</h4>
      {examCodes.map((code, index) => (
        <div key={index} className="exam-code-box">
          <div className="form-group-row">
            <label>
              Số lượng câu hỏi:
              <input
                type="number"
                min="1"
                className="form-input"
                value={code.questions}
                onChange={(e) =>
                  handleQuestionCountChange(index, e.target.value)
                }
              />
            </label>
            <label>
              Tổng điểm:
              <input
                type="number"
                min="0"
                className="form-input"
                value={code.totalScore}
                onChange={(e) => handleTotalScoreChange(index, e.target.value)}
              />
            </label>
            <span className="score-per-question">
              Mỗi câu: {calculateScorePerQuestion(code)} điểm
            </span>
          </div>

          {/* Các câu hỏi và đáp án */}
          {code.questionsData.map((question, qIndex) => (
            <div key={qIndex} className="question-box">
              <div className="form-group">
                <label>Câu hỏi {qIndex + 1}:</label>
                <input
                  type="text"
                  className="form-input"
                  value={question.questionText}
                  onChange={(e) => handleQuestionDataChange(index, qIndex, "questionText", e.target.value)}
                  placeholder={`Nhập câu hỏi ${qIndex + 1}`}
                />
              </div>

              <div className="form-group-row">
                {["A", "B", "C", "D"].map((opt) => (
                  <div key={opt} className="form-group">
                    <label>{opt}:</label>
                    <input
                      type="text"
                      className="form-input"
                      value={question.options[opt]}
                      onChange={(e) =>
                        handleQuestionDataChange(index, qIndex, "options", {
                          ...question.options,
                          [opt]: e.target.value,
                        })
                      }
                      placeholder={`Nhập đáp án ${opt}`}
                    />
                  </div>
                ))}
              </div>

              <div className="form-group">
                <label>Đáp án đúng:</label>
                <select
                  value={question.correctAnswer}
                  onChange={(e) =>
                    handleQuestionDataChange(index, qIndex, "correctAnswer", e.target.value)
                  }
                  className="form-input"
                >
                  <option value="A">A</option>
                  <option value="B">B</option>
                  <option value="C">C</option>
                  <option value="D">D</option>
                </select>
              </div>
            </div>
          ))}
        </div>
      ))}

      <button onClick={addExamCode} className="button-green">
        + Tạo Mã Đề Mới
      </button>

      <div className="form-group-row">
        <button onClick={handleSave} className="button-black">
          Lưu
        </button>
        <button onClick={handleCancel} className="button-gray">
          Hủy
        </button>
      </div>
    </div>
  );
}

export default CreateExamForm;
