import React, { useState } from "react";

function QuizApp({ examConfig, studentInfo }) {
  // X√°c ƒë·ªãnh m√£ ƒë·ªÅ ƒëang d√πng
  const codeIndex = parseInt(studentInfo.examCode.replace("MD", "")) - 1;
  const currentCode = examConfig.examCodes[codeIndex];

  // M·ªói c√¢u h·ªèi ƒë∆∞·ª£c tr·∫£ l·ªùi b·ªüi A/B/C/D ho·∫∑c null
  const [answers, setAnswers] = useState(
    Array(currentCode.questionsData.length).fill(null)
  );

  const handleAnswerChange = (index, value) => {
    const updated = [...answers];
    updated[index] = value;
    setAnswers(updated);
  };

  const handleSubmit = () => {
    console.log("B√†i l√†m:", answers);

    // Ch·∫•m ƒëi·ªÉm
    const correctAnswers = currentCode.questionsData.map(
      (q) => ["A", "B", "C", "D"][q.correctAnswer]
    );
    const correctCount = answers.filter(
      (answer, idx) => answer === correctAnswers[idx]
    ).length;

    const scorePerQuestion =
      currentCode.totalScore / currentCode.questionsData.length;
    const finalScore = (correctCount * scorePerQuestion).toFixed(2);

    alert(`‚úÖ N·ªôp b√†i th√†nh c√¥ng! ƒêi·ªÉm s·ªë: ${finalScore}/${currentCode.totalScore}`);
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>üìù B√†i thi: {examConfig.examName}</h2>
      <p>
        <strong>Sinh vi√™n:</strong> {studentInfo.studentName} (
        {studentInfo.studentId})
      </p>
      <p>
        <strong>M√£ ƒë·ªÅ:</strong> {studentInfo.examCode}
      </p>
      <p>
        <strong>Ph√≤ng thi:</strong> {studentInfo.roomName}
      </p>

      <hr />
      <h3>C√¢u h·ªèi</h3>

      {currentCode.questionsData?.map((question, idx) => (
        <div key={idx} style={{ marginBottom: 20 }}>
          <p>
            <strong>C√¢u {idx + 1}:</strong>{" "}
            {question.questionText || "Kh√¥ng c√≥ n·ªôi dung"}
          </p>
          <div>
            {["A", "B", "C", "D"].map((opt, optIdx) => (
              <label key={opt} style={{ marginRight: 15 }}>
                <input
                  type="radio"
                  name={`q${idx}`}
                  value={opt}
                  checked={answers[idx] === opt}
                  onChange={() => handleAnswerChange(idx, opt)}
                />
                {opt}. {question.options?.[optIdx] || ""}
              </label>
            ))}
          </div>
        </div>
      ))}

      <button onClick={handleSubmit} className="submit-button">
        N·ªôp b√†i
      </button>
    </div>
  );
}

export default QuizApp;
