import React, { useEffect, useState } from "react";

function QuizApp() {
  const studentData = {
    studentName: "Nguy·ªÖn VƒÉn A",
    studentId: "B21DCCN001",
    roomName: "Ph√≤ng 101",
    examCode: "DE001",
  };

  const examConfig = {
    examCodes: [
      {
        code: "DE001",
        questionsData: [
          {
            questionText: "1 + 1 b·∫±ng m·∫•y?",
            options: ["1", "2", "3", "4"],
            correctAnswer: 1,
          },
          {
            questionText: "Th·ªß ƒë√¥ c·ªßa Vi·ªát Nam l√† g√¨?",
            options: ["H√† N·ªôi", "TP. H·ªì Ch√≠ Minh", "ƒê√† N·∫µng", "Hu·∫ø"],
            correctAnswer: 0,
          },
          {
            questionText: "M√†u n√†o th∆∞·ªùng th·∫•y c·ªßa b·∫ßu tr·ªùi ban ng√†y?",
            options: ["ƒê·ªè", "V√†ng", "Xanh d∆∞∆°ng", "T√≠m"],
            correctAnswer: 2,
          },
          {
            questionText: "HTML l√† vi·∫øt t·∫Øt c·ªßa?",
            options: [
              "Hyper Text Markup Language",
              "High Text Machine Language",
              "Hyperlink and Text Markup Language",
              "Home Tool Markup Language",
            ],
            correctAnswer: 0,
          },
          {
            questionText: "Ph√≠m t·∫Øt sao ch√©p tr√™n Windows l√†?",
            options: ["Ctrl + V", "Ctrl + C", "Ctrl + X", "Ctrl + Z"],
            correctAnswer: 1,
          },
        ],
      },
    ],
  };

  const [timeLeft, setTimeLeft] = useState(30 * 60); // 30 ph√∫t
  const [isExamOver, setIsExamOver] = useState(false);
  const [studentAnswers, setStudentAnswers] = useState([]);
  const [score, setScore] = useState(null); // s·ªë c√¢u ƒë√∫ng
  const [isPassed, setIsPassed] = useState(null); // true: ƒë·∫°t, false: kh√¥ng ƒë·∫°t

  useEffect(() => {
    if (!isExamOver) {
      const timer = setInterval(() => {
        setTimeLeft((prevTime) => {
          if (prevTime <= 0) {
            clearInterval(timer);
            handleSubmit();
            return 0;
          }
          return prevTime - 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [isExamOver]);

  const handleAnswerChange = (questionIndex, answerIndex) => {
    const newAnswers = [...studentAnswers];
    newAnswers[questionIndex] = answerIndex;
    setStudentAnswers(newAnswers);
  };

  const handleSubmit = () => {
    setIsExamOver(true);
    const correctCount = exam.questionsData.reduce((count, q, index) => {
      return count + (studentAnswers[index] === q.correctAnswer ? 1 : 0);
    }, 0);
    setScore(correctCount);
    const isPass = correctCount >= Math.ceil(exam.questionsData.length * 0.6);
    setIsPassed(isPass);
  };

  const handleRetry = () => {
    setStudentAnswers([]);
    setIsExamOver(false);
    setTimeLeft(30 * 60);
    setScore(null);
    setIsPassed(null);
  };

  const exam = examConfig.examCodes.find(
    (e) => e.code === studentData.examCode
  );

  if (!exam || !exam.questionsData)
    return <p>Kh√¥ng t√¨m th·∫•y ƒë·ªÅ thi ph√π h·ª£p!</p>;

  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    return `${minutes}:${seconds < 10 ? `0${seconds}` : seconds}`;
  };

  return (
    <div className="quiz-container" style={{ maxWidth: 700, margin: "0 auto" }}>
      <h2>
        ƒê·ªÅ {studentData.examCode} - Ph√≤ng {studentData.roomName}
      </h2>
      <p>
        H·ªç t√™n: {studentData.studentName} - MSSV: {studentData.studentId}
      </p>
      <p>S·ªë c√¢u h·ªèi: {exam.questionsData.length}</p>

      <div className="timer">
        <h4>Th·ªùi gian c√≤n l·∫°i: {formatTime(timeLeft)}</h4>
        {isExamOver && <p>Th·ªùi gian ƒë√£ h·∫øt!</p>}
      </div>

      {exam.questionsData.map((q, index) => (
        <div key={index} className="question-box" style={{ marginBottom: 15 }}>
          <h4>
            C√¢u {index + 1}: {q.questionText}
          </h4>
          {q.options.map((opt, i) => (
            <div key={i}>
              <label>
                <input
                  type="radio"
                  name={`q${index}`}
                  value={i}
                  checked={studentAnswers[index] === i}
                  onChange={() => handleAnswerChange(index, i)}
                  disabled={isExamOver}
                />
                {String.fromCharCode(65 + i)}. {opt}
              </label>
            </div>
          ))}
        </div>
      ))}

      {!isExamOver ? (
        <button
          className="submit-button"
          onClick={handleSubmit}
          style={{
            backgroundColor: "#4CAF50",
            color: "white",
            padding: "10px 20px",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          N·ªôp b√†i
        </button>
      ) : (
        <>
          <div
            style={{
              marginTop: 20,
              padding: "10px",
              borderRadius: "5px",
              backgroundColor: isPassed ? "#d4edda" : "#f8d7da",
              color: isPassed ? "#155724" : "#721c24",
              border: `1px solid ${isPassed ? "#c3e6cb" : "#f5c6cb"}`,
            }}
          >
            <strong>K·∫øt qu·∫£:</strong>{" "}
            {isPassed ? "ƒê·∫°t üéâ" : "Kh√¥ng ƒë·∫°t üò¢"} - B·∫°n tr·∫£ l·ªùi ƒë√∫ng {score}/
            {exam.questionsData.length} c√¢u.
          </div>
          <button
            onClick={handleRetry}
            style={{
              marginTop: 15,
              backgroundColor: "#007bff",
              color: "white",
              padding: "10px 20px",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Thi l·∫°i
          </button>
        </>
      )}
    </div>
  );
}

export default QuizApp;
