import React, { useState } from "react";

// Component đăng nhập
const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = () => {
    // Giả sử đăng nhập thành công và trả về role
    const user = { username, role: "user" }; // Có thể thay đổi role là "admin" hoặc "user"
    onLogin(user);
  };

  return (
    <div>
      <h3>Đăng nhập</h3>
      <input 
        type="text" 
        placeholder="Username" 
        value={username} 
        onChange={(e) => setUsername(e.target.value)} 
      />
      <input 
        type="password" 
        placeholder="Password" 
        value={password} 
        onChange={(e) => setPassword(e.target.value)} 
      />
      <button onClick={handleLogin}>Đăng nhập</button>
    </div>
  );
};

// Component đăng ký
const Register = ({ onRegister }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleRegister = () => {
    // Giả sử đăng ký thành công
    const user = { username, role: "user" };
    onRegister(user);
  };

  return (
    <div>
      <h3>Đăng ký</h3>
      <input 
        type="text" 
        placeholder="Username" 
        value={username} 
        onChange={(e) => setUsername(e.target.value)} 
      />
      <input 
        type="password" 
        placeholder="Password" 
        value={password} 
        onChange={(e) => setPassword(e.target.value)} 
      />
      <button onClick={handleRegister}>Đăng ký</button>
    </div>
  );
};

// Component tạo phòng thi
const CreateExamRoom = ({ setStudentInfo, goToQuizApp }) => {
  const [studentName, setStudentName] = useState("");
  const [studentId, setStudentId] = useState("");
  const [roomName, setRoomName] = useState("");
  const [examCode, setExamCode] = useState("");

  const handleCreateRoom = () => {
    const studentData = { studentName, studentId, roomName, examCode };
    setStudentInfo(studentData);
    goToQuizApp();
  };

  return (
    <div>
      <h3>Tạo phòng thi</h3>
      <form onSubmit={(e) => e.preventDefault()}>
        <label>
          Họ và tên:
          <input 
            type="text" 
            value={studentName} 
            onChange={(e) => setStudentName(e.target.value)} 
            required 
          />
        </label>
        <label>
          Mã sinh viên:
          <input 
            type="text" 
            value={studentId} 
            onChange={(e) => setStudentId(e.target.value)} 
            required 
          />
        </label>
        <label>
          Tên phòng thi:
          <input 
            type="text" 
            value={roomName} 
            onChange={(e) => setRoomName(e.target.value)} 
            required 
          />
        </label>
        <label>
          Mã đề thi:
          <input 
            type="text" 
            value={examCode} 
            onChange={(e) => setExamCode(e.target.value)} 
            required 
          />
        </label>
        <button type="submit" onClick={handleCreateRoom}>Tạo phòng thi</button>
      </form>
    </div>
  );
};

// Component làm bài thi
const QuizApp = ({ examConfig, studentInfo, setStudentAnswers, goToResult }) => {
  const [answers, setAnswers] = useState([]);

  const handleSubmit = () => {
    setStudentAnswers(answers);
    goToResult();
  };

  return (
    <div>
      <h3>Vào phòng thi: {studentInfo.roomName}</h3>
      <h4>Mã đề thi: {studentInfo.examCode}</h4>
      <form onSubmit={(e) => e.preventDefault()}>
        {examConfig.questions.map((q, idx) => (
          <div key={idx}>
            <p>{q.questionText}</p>
            <label>
              A
              <input 
                type="radio" 
                name={`question${idx}`} 
                value="0" 
                onChange={() => setAnswers(prev => { 
                  const newAnswers = [...prev];
                  newAnswers[idx] = 0;
                  return newAnswers;
                })} 
              />
            </label>
            <label>
              B
              <input 
                type="radio" 
                name={`question${idx}`} 
                value="1" 
                onChange={() => setAnswers(prev => { 
                  const newAnswers = [...prev];
                  newAnswers[idx] = 1;
                  return newAnswers;
                })} 
              />
            </label>
            <label>
              C
              <input 
                type="radio" 
                name={`question${idx}`} 
                value="2" 
                onChange={() => setAnswers(prev => { 
                  const newAnswers = [...prev];
                  newAnswers[idx] = 2;
                  return newAnswers;
                })} 
              />
            </label>
          </div>
        ))}
        <button type="submit" onClick={handleSubmit}>Nộp bài</button>
      </form>
    </div>
  );
};

// Component kết quả làm bài
const Result = ({ studentInfo, studentAnswers, examConfig }) => {
  const score = examConfig.questions.reduce((acc, q, idx) => {
    return q.correctAnswer === studentAnswers[idx] ? acc + 1 : acc;
  }, 0);

  return (
    <div>
      <h3>Kết quả bài thi</h3>
      <p><strong>Họ tên:</strong> {studentInfo.studentName}</p>
      <p><strong>MSSV:</strong> {studentInfo.studentId}</p>
      <p><strong>Phòng thi:</strong> {studentInfo.roomName}</p>
      <p><strong>Mã đề:</strong> {studentInfo.examCode}</p>
      <p><strong>Điểm:</strong> {score} / {examConfig.questions.length}</p>
    </div>
  );
};

// App chính
const App = () => {
  const [currentPage, setCurrentPage] = useState("home");
  const [currentUser, setCurrentUser] = useState(null);
  const [examConfig, setExamConfig] = useState({
    questions: [
      { questionText: "Câu 1", correctAnswer: 0 },
      { questionText: "Câu 2", correctAnswer: 1 },
    ],
  });
  const [studentInfo, setStudentInfo] = useState(null);
  const [studentAnswers, setStudentAnswers] = useState([]);

  const handleLogin = (user) => {
    setCurrentUser(user);
    setCurrentPage("home");
  };

  const handleRegister = (user) => {
    setCurrentUser(user);
    setCurrentPage("home");
  };

  const handleLogout = () => {
    setCurrentUser(null);
    setCurrentPage("home");
  };

  const handleCreateRoom = () => {
    if (!currentUser) {
      return alert("Bạn cần đăng nhập để tạo phòng thi.");
    }

    if (currentUser.role !== "admin" && currentUser.role !== "user") {
      return alert("⚠️ Bạn phải là giáo viên mới có thể tạo phòng thi.");
    }

    setCurrentPage("createRoom");
  };

  return (
    <div>
      <div>
        <button onClick={() => setCurrentPage("home")}>Trang chủ</button>
        {!currentUser ? (
          <>
            <button onClick={() => setCurrentPage("login")}>Đăng nhập</button>
            <button onClick={() => setCurrentPage("register")}>Đăng ký</button>
          </>
        ) : (
          <>
            <button onClick={handleLogout}>Đăng xuất</button>
            {currentUser.role === "admin" || currentUser.role === "user" ? (
              <button onClick={handleCreateRoom}>Tạo phòng thi</button>
            ) : null}
          </>
        )}
      </div>

      {currentPage === "home" && <h2>Chào mừng đến với QuizApp!</h2>}
      {currentPage === "login" && <Login onLogin={handleLogin} />}
      {currentPage === "register" && <Register onRegister={handleRegister} />}
      {currentPage === "createRoom" && (
        <CreateExamRoom setStudentInfo={setStudentInfo} goToQuizApp={() => setCurrentPage("quiz")} />
      )}
      {currentPage === "quiz" && (
        <QuizApp examConfig={examConfig} studentInfo={studentInfo} setStudentAnswers={setStudentAnswers} goToResult={() => setCurrentPage("result")} />
      )}
      {currentPage === "result" && (
        <Result studentInfo={studentInfo} studentAnswers={studentAnswers} examConfig={examConfig} />
      )}
    </div>
  );
};

export default App;
