import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./App.css";

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [showLogin, setShowLogin] = useState(false);
  const [loginError, setLoginError] = useState("");
  const navigate = useNavigate();

  const toggleLogin = () => setShowLogin(!showLogin);

  const handleLogin = (e) => {
    e.preventDefault();
    const username = e.target.username.value;
    const password = e.target.password.value;
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser?.username === username && storedUser?.password === password) {
      setCurrentUser(storedUser);
      setShowLogin(false);
      setLoginError("");
    } else {
      setLoginError("Sai tên đăng nhập hoặc mật khẩu.");
    }
  };

  const handleCreateQuiz = () => {
    if (!currentUser) setShowLogin(true);
    else if (currentUser.role !== "admin") alert("Bạn phải là admin để tạo đề thi.");
    else navigate("/quizform");
  };

  return (
    <div>
      <div className="navbar">
        <h2>QuizApp</h2>
        <button onClick={handleCreateQuiz}>Tạo đề thi</button>
      </div>

      {showLogin && (
        <div className="modal">
          <form onSubmit={handleLogin}>
            <input name="username" placeholder="Tên đăng nhập" required />
            <input name="password" type="password" placeholder="Mật khẩu" required />
            {loginError && <p style={{ color: "red" }}>{loginError}</p>}
            <button type="submit">Đăng nhập</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default App;
