import React, { useEffect, useState } from "react";

function QuizApp({ onRetry, onCreateNewRoom }) {
  const [studentData, setStudentData] = useState({
    studentName: "Nguyễn Văn A",
    studentId: "123456789",
    examCode: "EX123",
    roomName: "Phòng 1",
  });

  const [examConfig, setExamConfig] = useState({
    examCodes: [
      {
        code: "EX123",
        questionsData: [
          {
            questionText: "Câu hỏi 1: question la cai cho gi = ?",
            options: [
              "mot cai gi do rat xam lz",
              "dung hoi nưa khong muon biet",
              "len chatgpt di choi ",
              "hahahahahahaha"
            ],
            correctAnswer: 1,
          },
          {
            questionText: "Câu hỏi 2: 3 + 5 = ?",
            options: ["7", "8", "9", "10"],
            correctAnswer: 1,
          },
          {
            questionText: "Câu hỏi 3: 5 + 7 = ?",
            options: ["11", "12", "13", "14"],
            correctAnswer: 1,
          },
        ],
      },
    ],
  });

  const [timeLeft, setTimeLeft] = useState(30 * 60);
  const [isExamOver, setIsExamOver] = useState(false);
  const [studentAnswers, setStudentAnswers] = useState([]);
  const [score, setScore] = useState(null);
  const [isPassed, setIsPassed] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    if (isExamOver) return;
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => {
        if (prevTime <= 0) {
          clearInterval(timer);
          handleSubmit();
          return 0;
        }
        return prevTime - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [isExamOver]);

  const handleAnswerChange = (questionIndex, answerIndex) => {
    const newAnswers = [...studentAnswers];
    newAnswers[questionIndex] = answerIndex;
    setStudentAnswers(newAnswers);
  };

  const handleSubmit = () => {
    setIsExamOver(true);
    const exam = examConfig.examCodes.find(
      (e) => e.code === studentData.examCode
    );

    let correctCount = 0;
    exam.questionsData.forEach((q, index) => {
      if (studentAnswers[index] === q.correctAnswer) {
        correctCount++;
      }
    });

    const total = exam.questionsData.length;
    const passed = correctCount >= Math.ceil(total / 2);

    setScore(correctCount);
    setIsPassed(passed);
  };

  const handleRetry = () => {
    setTimeLeft(30 * 60);
    setIsExamOver(false);
    setStudentAnswers([]);
    setScore(null);
    setIsPassed(null);

    if (onRetry) onRetry();
  };

  const handleCreateNewRoom = () => {
    setStudentData({
      studentName: "",
      studentId: "",
      examCode: "",
      roomName: "",
    });

    if (onCreateNewRoom) onCreateNewRoom();
  };

  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    return `${minutes}:${seconds < 10 ? `0${seconds}` : seconds}`;
  };

  const handleJumpToQuestion = (index) => {
    const element = document.getElementById(`question-${index}`);
    if (element) {
      element.scrollIntoView({ behavior: "smooth", block: "center" });
      setIsMenuOpen(false);
    }
  };

  return (
    <div className="quiz-container p-4">
      <div className="flex items-center justify-between mb-2 relative">
        <div>
          <h2>
            Đề {studentData.examCode} - Phòng {studentData.roomName}
          </h2>
          <p>
            Họ tên: {studentData.studentName} - MSSV: {studentData.studentId}
          </p>
          <p>Số câu hỏi: {examConfig.examCodes[0].questionsData.length}</p>
        </div>

        {/* Menu icon + danh sách câu hỏi */}
        <div className="relative">
          <button
            className="text-2xl px-3 py-1 rounded bg-gray-200 hover:bg-gray-300"
            onClick={() => setIsMenuOpen(!isMenuOpen)}
          >
            ☰
          </button>

          {isMenuOpen && (
            <div className="absolute top-full right-0 mt-2 w-48 bg-white border rounded shadow z-10">
              <h4 className="p-2 border-b font-semibold">Danh sách câu hỏi</h4>
              <ul>
                {examConfig.examCodes[0].questionsData.map((_, idx) => (
                  <li
                    key={idx}
                    className="px-4 py-2 hover:bg-gray-100 cursor-pointer"
                    onClick={() => handleJumpToQuestion(idx)}
                  >
                    Câu {idx + 1}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>

      <div className="timer mb-4">
        <h4>Thời gian còn lại: {formatTime(timeLeft)}</h4>
        {isExamOver && <p>Thời gian đã hết!</p>}
      </div>

      {examConfig.examCodes[0].questionsData.map((q, index) => (
        <div
          key={index}
          id={`question-${index}`}
          className="question-box1 mb-4 border p-4 rounded bg-gray-50"
        >
          <h4>
            Câu {index + 1}: {q.questionText}
          </h4>
          <div className="answer-grid grid gap-2 mt-2">
            {q.options.map((opt, i) => (
              <label key={i} className="answer-option block">
                <input
                  type="radio"
                  name={`q${index}`}
                  value={i}
                  checked={studentAnswers[index] === i}
                  onChange={() => handleAnswerChange(index, i)}
                  disabled={isExamOver}
                  className="mr-2"
                />
                <span>
                  {String.fromCharCode(65 + i)}. {opt}
                </span>
              </label>
            ))}
          </div>
        </div>
      ))}

      {!isExamOver && (
        <button
          className="submit-button bg-blue-500 text-white p-2 mt-4 rounded"
          onClick={handleSubmit}
        >
          Nộp bài
        </button>
      )}

      {isExamOver && score !== null && (
        <div className="result mt-4 p-4 border rounded bg-white">
          <h3>Thông tin thí sinh:</h3>
          <p><strong>Họ tên:</strong> {studentData.studentName}</p>
          <p><strong>MSSV:</strong> {studentData.studentId}</p>
          <p><strong>Mã đề:</strong> {studentData.examCode}</p>
          <p><strong>Phòng thi:</strong> {studentData.roomName}</p>
          <h3>
            Kết quả:{" "}
            <span
              style={{
                color: isPassed ? "green" : "red",
                fontWeight: "bold",
                fontSize: "18px",
              }}
            >
              {isPassed ? "ĐẠT" : "KHÔNG ĐẠT"}
            </span>{" "}
            ({score}/{examConfig.examCodes[0].questionsData.length} câu đúng)
          </h3>
          <button
            onClick={handleRetry}
            className="retry-button bg-yellow-500 text-white p-2 mt-2 rounded"
          >
            Thi lại
          </button>
        </div>
      )}
    </div>
  );
}

export default QuizApp;
