import React, { useState } from "react";

function CreateExamForm({ setExamConfig, goToCreateForm }) {
  const [examName, setExamName] = useState("");
  const [grade, setGrade] = useState("");
  const [subject, setSubject] = useState("");
  const [examCodes, setExamCodes] = useState([
    {
      id: 1,
      questions: 5,
      totalScore: 8,
      questionsData: Array(5).fill({
        questionText: "",
        options: { A: "", B: "", C: "", D: "" },
        correctAnswer: "A",
      }),
    },
  ]);

  const handleQuestionCountChange = (index, value) => {
    const updated = [...examCodes];
    const num = parseInt(value);
    updated[index].questions = num;
    updated[index].questionsData = Array(num).fill({
      questionText: "",
      options: { A: "", B: "", C: "", D: "" },
      correctAnswer: "A",
    });
    setExamCodes(updated);
  };

  const handleSave = () => {
    const examData = {
      examName,
      grade,
      subject,
      examCodes,
    };

    setExamConfig(examData); // Truyền dữ liệu về App.js
    goToCreateForm(); // Điều hướng sang CreateForm

    console.log("Đề thi đã được lưu:", examData);
  };

  return (
    <div>
      <h2>Tạo đề thi</h2>
      {/* Nhập tên đề, khối, môn học */}
      <input value={examName} onChange={(e) => setExamName(e.target.value)} placeholder="Tên đề thi" />
      <input value={grade} onChange={(e) => setGrade(e.target.value)} placeholder="Khối học" />
      <input value={subject} onChange={(e) => setSubject(e.target.value)} placeholder="Môn học" />

      {/* Quản lý mã đề */}
      {examCodes.map((code, index) => (
        <div key={index}>
          <input
            type="number"
            value={code.questions}
            onChange={(e) => handleQuestionCountChange(index, e.target.value)}
          />
          {/* Nhập câu hỏi và đáp án */}
          {code.questionsData.map((question, qIndex) => (
            <div key={qIndex}>
              <input
                type="text"
                value={question.questionText}
                onChange={(e) => {
                  const updated = [...examCodes];
                  updated[index].questionsData[qIndex].questionText = e.target.value;
                  setExamCodes(updated);
                }}
                placeholder={`Câu hỏi ${qIndex + 1}`}
              />
              {["A", "B", "C", "D"].map((opt) => (
                <input
                  key={opt}
                  type="text"
                  value={question.options[opt]}
                  onChange={(e) => {
                    const updated = [...examCodes];
                    updated[index].questionsData[qIndex].options[opt] = e.target.value;
                    setExamCodes(updated);
                  }}
                  placeholder={`Đáp án ${opt}`}
                />
              ))}
              <select
                value={question.correctAnswer}
                onChange={(e) => {
                  const updated = [...examCodes];
                  updated[index].questionsData[qIndex].correctAnswer = e.target.value;
                  setExamCodes(updated);
                }}
              >
                <option value="A">A</option>
                <option value="B">B</option>
                <option value="C">C</option>
                <option value="D">D</option>
              </select>
            </div>
          ))}
        </div>
      ))}

      <button onClick={handleSave}>Lưu Đề Thi</button>
    </div>
  );
}

export default CreateExamForm;
