import React, { useEffect, useState } from "react";

function QuizApp() {
  const [studentData, setStudentData] = useState(null);
  const [examConfig, setExamConfig] = useState(null);
  const [timeRemaining, setTimeRemaining] = useState(30 * 60); // 30 minutes in seconds
  const [isFinished, setIsFinished] = useState(false); // Trạng thái bài thi đã hoàn thành hay chưa

  useEffect(() => {
    // Test dữ liệu mẫu để kiểm tra hiển thị
    const fakeStudent = {
      studentName: "Nguyễn Văn A",
      studentId: "SV001",
      examCode: "MD01",
      roomName: "P101",
    };

    const fakeExam = {
      examCodes: [
        {
          code: "MD01",
          questionsData: [
            {
              questionText: "1 + 1 = ?",
              options: ["1", "2", "3", "4"],
              correctAnswer: 1,
            },
            {
              questionText: "Thủ đô Việt Nam là?",
              options: ["Hà Nội", "Hồ Chí Minh", "Đà Nẵng", "Huế"],
              correctAnswer: 0,
            },
          ],
        },
      ],
    };

    // Lưu vào localStorage để test
    localStorage.setItem("studentData", JSON.stringify(fakeStudent));
    localStorage.setItem("examConfig", JSON.stringify(fakeExam));

    // Gán vào state
    setStudentData(fakeStudent);
    setExamConfig(fakeExam);

    // Bộ đếm thời gian
    const interval = setInterval(() => {
      setTimeRemaining((prevTime) => {
        if (prevTime <= 1) {
          clearInterval(interval);
          setIsFinished(true);
          return 0; // Khi hết thời gian
        }
        return prevTime - 1;
      });
    }, 1000); // giảm mỗi giây

    // Dọn dẹp interval khi component unmount
    return () => clearInterval(interval);
  }, []);

  // Chuyển thời gian còn lại sang định dạng phút:giây
  const formatTime = (timeInSeconds) => {
    const minutes = Math.floor(timeInSeconds / 60);
    const seconds = timeInSeconds % 60;
    return `${minutes}:${seconds < 10 ? "0" : ""}${seconds}`;
  };

  if (!studentData || !examConfig) return <p>Đang tải dữ liệu...</p>;

  const exam = examConfig.examCodes.find(
    (e) => e.code === studentData.examCode
  );

  if (!exam || !exam.questionsData) return <p>Không tìm thấy đề thi phù hợp!</p>;

  const handleSubmit = () => {
    setIsFinished(true);
    alert("✅ Bạn đã nộp bài thành công!");
  };

  return (
    <div className="quiz-container">
      <h2>
        Đề {studentData.examCode} - Phòng {studentData.roomName}
      </h2>
      <p>
        Họ tên: {studentData.studentName} - MSSV: {studentData.studentId}
      </p>
      <p>
        Số câu hỏi: {exam.questionsData.length}
      </p>

      {/* Hiển thị thời gian còn lại */}
      <div className="time-remaining">
        <h3>Thời gian còn lại: {formatTime(timeRemaining)}</h3>
      </div>

      {exam.questionsData.map((q, index) => (
        <div key={index} className="question-box">
          <h4>Câu {index + 1}: {q.questionText}</h4>
          <div className="options-horizontal">
            {q.options.map((opt, i) => (
              <label key={i}>
                <input type="radio" name={`q${index}`} value={i} />
                {String.fromCharCode(65 + i)}. {opt}
              </label>
            ))}
          </div>
        </div>
      ))}

      {/* Nút nộp bài */}
      {!isFinished && (
        <button className="submit-button" onClick={handleSubmit}>
          Nộp bài
        </button>
      )}
      {isFinished && <p>Bạn đã nộp bài!</p>}
    </div>
  );
}

export default QuizApp;
