import React, { useState } from "react";
import "./QuizApp.css";

function CreateExamForm({ goToCreateForm, setExamConfig }) {
  const [examName, setExamName] = useState("");
  const [grade, setGrade] = useState("");
  const [subject, setSubject] = useState("");
  const [questionsCount, setQuestionsCount] = useState(1);
  const [totalScore, setTotalScore] = useState(10);

  const handleNext = () => {
    if (!examName || !grade || !subject) {
      alert("Vui lòng điền đầy đủ thông tin đề thi.");
      return;
    }

    const examInfo = {
      examName,
      grade,
      subject,
      questionsCount: parseInt(questionsCount),
      totalScore: parseFloat(totalScore),
    };

    // Lưu thông tin cấu hình đề thi vào state cha
    setExamConfig(examInfo);

    // Chuyển sang trang nhập câu hỏi
    goToCreateForm();
  };

  return (
    <div className="create-exam-container">
      <h2 className="form-title">Tạo đề thi</h2>

      <div className="form-group">
        <label>Tên đề:</label>
        <input
          className="form-input"
          value={examName}
          onChange={(e) => setExamName(e.target.value)}
          placeholder="Nhập tên đề..."
        />
      </div>

      <div className="form-group-row">
        <div>
          <label>Khối học:</label>
          <input
            className="form-input"
            value={grade}
            onChange={(e) => setGrade(e.target.value)}
            placeholder="VD: 10"
          />
        </div>
        <div>
          <label>Môn học:</label>
          <input
            className="form-input"
            value={subject}
            onChange={(e) => setSubject(e.target.value)}
            placeholder="VD: Toán"
          />
        </div>
      </div>

      <div className="form-group-row">
        <div>
          <label>Số lượng câu hỏi:</label>
          <input
            type="number"
            className="form-input"
            min={1}
            value={questionsCount}
            onChange={(e) => setQuestionsCount(e.target.value)}
          />
        </div>
        <div>
          <label>Tổng điểm:</label>
          <input
            type="number"
            className="form-input"
            min={0}
            value={totalScore}
            onChange={(e) => setTotalScore(e.target.value)}
          />
        </div>
      </div>

      <button className="button-green" onClick={handleNext}>
        Tiếp tục nhập câu hỏi
      </button>
    </div>
  );
}

export default CreateExamForm;
