// CreateExamRoom.js
import React, { useState, useEffect } from "react";
import "./QuizApp.css";

function CreateExamRoom({ startExamRoom }) {
  const [examList, setExamList] = useState([]);
  const [selectedExamIndex, setSelectedExamIndex] = useState(null);

  useEffect(() => {
    const savedExams = JSON.parse(localStorage.getItem("exams")) || [];
    setExamList(savedExams);
  }, []);

  const handleStartRoom = () => {
    if (selectedExamIndex !== null) {
      const selectedExam = examList[selectedExamIndex];
      startExamRoom(selectedExam);
    } else {
      alert("Vui lòng chọn một đề để tạo phòng thi.");
    }
  };

  return (
    <div className="create-exam-container">
      <h2 className="form-title">Tạo phòng thi</h2>

      {examList.length === 0 ? (
        <p>Chưa có đề thi nào. Hãy tạo đề thi trước.</p>
      ) : (
        <>
          <label>Chọn đề thi:</label>
          <select
            className="input-field"
            onChange={(e) => setSelectedExamIndex(e.target.value)}
            defaultValue=""
          >
            <option value="" disabled>-- Chọn đề --</option>
            {examList.map((exam, index) => (
              <option key={index} value={index}>
                {exam.examName} - {exam.subject} - Lớp {exam.grade}
              </option>
            ))}
          </select>
          <button className="button-black" onClick={handleStartRoom}>
            Tạo phòng thi
          </button>
        </>
      )}
    </div>
  );
}

export default CreateExamRoom;
